INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Apartheid Museum', 'apartheid-museum', '', '<p>The Apartheid Museum is in Johannesburg and is dedicated to recounting the history of Apartheid and the Anti-Apartheid Movement.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa7b064c5aa63da62ea9_apartheid-museum.jpeg', 1, 1, 1, 26, 28, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Aqua Sol Theme Park', 'aqua-sol-theme-park', '', '<p>You can’t go on a vacation to a tropical paradise and not go to the local water parks. Aqua Sol Theme Park is one of the most thrilling and exciting places to visit, especially for children. This clean, well-maintained amusement park sits directly on the beach, right in front of the water for oceanfront views.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa625ee81181e2aacbe9_montego-bay-aqua-sol-park.jpeg', 1, 1, 1, 18, 78, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Arbor Crest Wine Cellars', 'arbor-crest-wine-cellars', '', '<p>This large winery in eastern Washington offers something for everyone, including taste-testing different wines, harvesting, and manufacturing the wine.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8f9dce7a1f6535388ff2f_arbor-crest-wine-cellars.jpeg', 1, 1, 1, 48, 117, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Arc de Triomphe', 'arc-de-triomphe', 'The Arc honors the soldiers who fought and died for France during the Napoleonic Wars and French Revolutionary.', '<p>The Arc of Triomphe is one of the most famous monuments in Paris. The Arc honors the soldiers who fought and died for France during the Napoleonic Wars and French Revolutionary.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa6ec4030a8d8f148c9c_arc-de-triomphe.jpeg', 4, 1, 4, 49, 2, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 3, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
