INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Archdiocesan Shrine', 'archdiocesan-shrine', '', '<p>San Agustin Church, also known as the Archdiocesan Shrine of Nuestra Señora de Consolacion y Correa or the Immaculate Conception Parish, is a Roman Catholic church under the auspices of The Order of St. Augustine, located inside the historic walled city of Intramuros in Manila. Take time to soak in the history and tranquility of this peaceful place.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa4bf4ed499ea71a0f3c_archdiocesan-shrine.jpeg', 1, 1, 1, 15, 121, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Arenal Volcano and Hot Springs', 'arenal-volcano-and-hot-springs', '', '<p>Visitors can drive a couple of hours outside San Jose to have adventure-filled days. Tour the Arenal Volcano, relax in the hot springs, hike on trails surrounded by rain forest, swim under waterfalls, zipline, or white-water raft for a fun-filled day of adventure.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa143c56655aae6b5c6b_arenal-volcano-hot-springs.jpeg', 1, 1, 1, 37, 122, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Arizona-Sonara Desert Museum', 'arizona-sonara-desert-museum', '', '<p>The museum is one of the largest natural history museums in the country. The museum has dis-plays, exhibits, and tours featuring the history and culture of Arizona.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8f9c4198c40d98e527249_tucson-arizona-sonora-desert-museum.jpeg', 1, 1, 1, 32, 111, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Arrington Vineyards and Winery', 'arrington-vineyards-and-winery', '', '<p>This absolutely stunning vineyard is owned by Kix Brooks, one half of the legendary Brooks and Dunn. It is located just a short 25 miles south of Nashville and includes 16 acres of vineyards. It is free to the public and includes many spots for picnic lunches, wandering, and enjoying wine-tasting events. Visit for food trucks and live music from April-November. It’s a treat to spend time here in the Tennessee countryside.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa8a3c566581576b6e42_3aa96f04-1da8-46ce-8637-b153945b2390.jpeg', 1, 1, 1, 36, 87, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Art Museums', 'art-museums', '', '<p>As mentioned above, Dallas is known for its great Art District. The Dallas Museum of Art is a top art gallery in the U.S. It is located down the street from the Crow Museum of Asian Art, which features exhibits from several countries in Asia.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa154ffa1630dd9924eb_art-museums.jpeg', 1, 1, 1, 33, 97, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Art Museums', 'art-museums-3', '', '<p>The Art Institute of Chicago, founded in 1879, is one of the city’s gems. It has over 300,000 items and hosts many special exhibits every year.The City Gallery, located in the Historic Water Tower, is a free showcase for local photographers and painters, and the structure is a beautiful display of architectural wonder. Constructed between 1867 and 1869, it was one of the few buildings to escape the fire of 1871. Afterwards, shop around Water Tower Place; America’s first vertical mall that is seventy-four floors high, with a vast array of shops and a packed food court.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa920da884fe52790c11_watertower.2.jpeg', 1, 1, 1, 42, 88, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Art Nouveau Ålesund', 'art-nouveau-alesund', '', '<p>Art Nouveau Ålesund is at the heart of Alesund and is surrounded by its beautiful architecture. For a small fee you could join a tour to explore the history of the town and its architecture or you can buy an informative guide and explore on your own. Either way you won’t be short of picture perfect opportunities!</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa273c56654c3d6b5f72_31182028727_6151f77fa1_c.jpeg', 1, 1, 1, 62, 6, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Art Walk', 'art-walk', '', '<p>Bozeman’s free art walks are usually held monthly by the Downtown Bozeman Association and Emerson Cultural Center. Tour Bozeman’s art galleries and browse for local art at these cultural community events. Check the website for a more detailed schedule.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8f9fcb3a4cbb2c1286a4d_640px-kirsten_kainz_-_tortoise_-_bozeman_montana_-_2013-07-09_9379328716.jpeg', 1, 1, 1, 46, 111, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Asen Tursti', 'asen-tursti', '', '<p>Asen Tursti is an easy hike that offers fantastic views of the surrounding area. The hike is a little over 3 miles long. It’s well marked, and the path is fairly level so many use this as a running or jogging path.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa973c5665b0ac6b6f47_hiking_tent_hardangervidda_norway_60.015_6.718.jpeg', 1, 1, 1, 66, 13, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Asheville Science Museum', 'asheville-science-museum', '', '<p>Children and adults alike will enjoy this family experience. This hands-on museum offers something for everyone, with interactive exhibits, a planetarium, and a variety of educational programs. The museum is also home to the world’s largest collection of minerals from North Carolina, making it a must-see for anyone interested in geology, andIt is considered one of the leading Science Museums in the United States.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa22198c405b2f527938_asheville-science-museum.jpeg', 1, 1, 1, 36, 83, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Asheville White Water Rafting', 'asheville-white-water-rafting', '', '<p>Asheville is a prime location for white water rafting, with rapids for every experience level. Tour groups here will take you to rivers under a half hour’s drive from the town center. The French Broad River is the most frequented, where you can tube or kayak down the rapids solo or with a group.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa7cf4ed4963f01a12b2_asheville-rafting.jpeg', 1, 1, 1, 41, 76, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Askari Monument', 'askari-monument', '', '<p>The Askari Monument, also known as Dar es Salaam African Memorial, is a memorial to the Askari who fought in World War I with British troops against the Germans.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa23dd02fa8022acaa34_askari-monument.jpeg', 1, 1, 1, 7, 39, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Astrup Fearnley Museum of Modern art', 'astrup-fearnley-museum-of-modern-art', '', '<p>The museum has won several awards for its modern architecture. It is located on the coast and features many kinds of modern art by Norwegian artists.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8f9f6cbe2f66973ece5f3_oslo-astrup-fearnley-museum.jpeg', 1, 1, 1, 60, 11, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Atkinson Clock Tower', 'atkinson-clock-tower', '', '<p>If you are looking for some more indoor activities, then take a look at Atkinson Clock Tower, a 1905 wooden clock tower named after a British colonial administrator. It is the oldest standing structure in Kota Kinabalu. It was originally known as the Atkinson Memorial Clock Tower and sits in solitary on the bluff along Signal Hill Road overlooking the seaside city.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fab109d76d752e3212b3_atkinson-clock-tower.jpeg', 1, 1, 1, 6, 116, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Atlanta Monetary Museum', 'atlanta-monetary-museum', '', '<p>The Atlanta Monetary Museum is also free. Historical artifacts and interactive multimedia exhibits are fun for the kids. Check out where millions of U.S. dollars are counted, sorted, and shredded. You can also receive a group tour of the vault and see the robots that are responsible for most of the production process.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8f9d0cf6b6cc336f00a4b_altanta-monetary-museum.jpeg', 1, 1, 1, 34, 84, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Atlantic City Boardwalk', 'atlantic-city-boardwalk', '', '<p>On June 26, 1870, the Atlantic City Boardwalk opened for business. The world-famous boardwalk stretches along five magical and glittering miles of the Atlantic City beachfront. Along with the several casinos, here you’ll find the Steel Pier amusement park, the Ripley’s Believe It or Not! Museum, and the Atlantic City Wax Museum. There’s also plenty of shopping to be done along the boardwalk, with countless souvenir shops, clothing stores, and more. Walk or bike the boardwalk during the day, (biking hours are 6 am-noon May-September).</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa18cd4253e18f884461_atlantic-boardwalk.jpeg', 1, 1, 1, 39, 74, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Atlantic Harbor Seals Exhibit', 'atlantic-harbor-seals-exhibit', '', '<p>Boston’s New England Aquarium charges for admission, but if you’re just passing by, you can see the massive outdoor harbor seal exhibit for free without even entering the museum. Native Atlantic harbor seals splash around the Central Wharf and sunbathe on rocky New England outcroppings, located just outside the main building.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8f9d5f4ed492f5f1a04be_0_2_2_3_new_england_aquarium_harbor_seal-1354x900.jpeg', 1, 1, 1, 42, 71, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Atlantis The Royal', 'atlantis-the-royal', 'The new iconic landmark of Dubai, Atlantis The Royal welcomes you to an experience that will completely redefine your perspective of luxury. Crafted by the world’s leading designers, architects and artists, this is a destination where everything has been designed to challenge the boundaries of imagination. Be taken on a journey of the impossible, with artful masterpieces, iconic entertainment and beautiful craftsmanship at every turn. Where the highest level of service sets a new standard in excellence. This is the place where something incredible happens at every moment of your stay. This is Atlantis The Royal.', '<p id="">The new iconic landmark of Dubai, Atlantis The Royal welcomes you to an experience that will completely redefine your perspective of luxury. Crafted by the world’s leading designers, architects and artists, this is a destination where everything has been designed to challenge the boundaries of imagination. Be taken on a journey of the impossible, with artful masterpieces, iconic entertainment and beautiful craftsmanship at every turn. Where the highest level of service sets a new standard in excellence. This is the place where something incredible happens at every moment of your stay. This is Atlantis The Royal.</p>', 'https://www.atlantis.com/-/media/atlantis/dubai/atp/videos/atlantis-aquaventure/aqv-trident-tower-film-2021_adult_website-hero-banner.mp4?sc_lang=en', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63cc87e0b0c560a933ff1283_atlantis%20the%20royal%20dubai.jpeg', 2, 1, 2, 0, 0, 'info@palmatlantis.com', '+971 4 426 3000', 'Crescent Rd - Palm Jumeirah - Dubai - United Arab Emirates', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 2, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Avenida Kukulkan', 'avenida-kukulkan', '', '<p>Stretching along the 13 mile island of Cancun, nearly all visitors will find themselves on this avenue at some point during their stay. But this major thoroughfare can be more than just a means of travel. For those willing to stroll along on foot, it also offers a fun, interactive view of the bustling tourist hub. Shops, restaurants, and stunning waterfront views abound, and it’s likely you’ll also stumble across some archaeological ruins while you’re at it.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa704ffa16beca9935a3_cancun-ik%2527kil.jpeg', 1, 1, 1, 21, 87, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Bako National Park', 'bako-national-park', '', '<p>Established in 1957, it is the oldest national park in Sarawak. It covers an area of 27.27 square kilometers. It is approximately 40 kilometers by road from Kuching. Millions of years of erosion of the sandstone have created a coastline of steep cliffs, rocky headlands and stretches of white, sandy bays. It is often visited as a daytrip from Kuching, though accommodations are available.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa73cbe2f689ebecfc77_bako-national-park.jpeg', 1, 1, 1, 2, 110, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Balboa Island Museum', 'balboa-island-museum', '', '<p>Balboa Island is located off the coast of Newport Beach and is one of the only museums in the county dedicated to telling the storied history and culture of the area. Exhibits include themes on fishing, surfing, and other waterfront activities as well as general history and exhibits for kids and families. Admission is free.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa32b3a4cb31cb286d16_orange-balboa-island-museum.jpeg', 1, 1, 1, 34, 118, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Balmoral Castle', 'balmoral-castle', '', '<p>Balmoral is King Charles’s summer residence, and every King and Queen going back several generations has stayed at the castle each summer.</p>', '', '', 1, 1, 1, 57, 3, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Baltimore Basilica', 'baltimore-basilica', '', '<p>Officially named The Basilica of the National Shrine of the Assumption of the Blessed Virgin Mary (admittedly a bit of a mouthful), this stunning work of American architecture was the first Catholic cathedral built in the newly formed US. The basilica, as well as the garden located on its beautiful grounds, is open to all who wish to experience its grandeur, learn its history, or just sit and enjoy the serenity. Free tours are available, but they must be scheduled in advance. And while visitors are not charged any fees, voluntary donations go a long way towards maintaining this historic site.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8f9cc185e3425c2b8a285_baltimore-basilica.jpeg', 1, 1, 1, 39, 77, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Baltimore Museum of Art', 'baltimore-museum-of-art', '', '<p>The arts have always been central to Baltimore’s history and culture, and thankfully, visitors to the BMA can enjoy them for free! With a regular rotation of new exhibits, as well as permanent installations like the outdoor Sculpture Gardens, tourists and locals alike will find themselves in awe of the many old and modern masterpieces found here.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8f9e0f62f25d74d275cc4_baltimore-valley-state-park.jpeg', 1, 1, 1, 39, 77, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Bandingilo National Park', 'bandingilo-national-park', '', '<p>Located in South Sudan’s Equatoria region, this park is sometimes spelled “Badingilo”. It was first established in 1992 and covers central Equatoria and Eastern Equatoria. Situated in a wooded area near the White Nile River, it is over 10,000 square kilometers in size. If you come the right time of year, you can view the earth’s second-largest annual animal migration, involving multiple species of antelope. The park is home to many other wildlife, including giraffes and predators like African dogs. Though a major wildlife preserve, the park lies within a total oil concession, potentially exposing it to surveying and drilling.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8f9f4fccaf9a246cd3765_juba-national-park_0.jpeg', 1, 1, 1, 6, 32, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Baneheia Outdoor Area', 'baneheia-outdoor-area', '', '<p>Baneheia was previously a pasture and woodcutting area until the 70’s when it was developed as a hiking and recreation area. There are different trails ranging from easy to hard depending on your ability. Swimming and fishing spots, lookout points, and thriving bird and animal life are also found here. There is something for everyone to enjoy.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8f9e3751715a6bec3d1e0_a.jpeg', 1, 1, 1, 44, 87, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Banyan Treehouse', 'banyan-treehouse', 'The suspended Banyan Treehouse is next to the beautiful waterfront and promenade, and a place the whole family can enjoy.', '<p>The suspended Banyan Treehouse is next to the beautiful waterfront and promenade, and a place the whole family can enjoy. The interactive 30-ft structure is built from an old Banyan tree in a fun park that is free to the public. It features boardwalks, balconies, and places to sit and enjoy the gorgeous garden.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fab5dd02fa4056acb845_banyan-tree.jpeg', 1, 1, 1, 21, 157, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Barbancourt Rum Distillery', 'barbancourt-rum-distillery', '', '<p>Rum is the number one produced alcoholic drink made in Haiti. The distillery produces some of the best rum in the world. You can tour the facilities and watch rum being processed. Samples are given to visitors at the end of the tour.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8faaee7a1f6656d891c22_port-au-prince-distillery.jpeg', 1, 1, 1, 19, 72, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Barefoot Landing', 'barefoot-landing', '', '<p>The walkable lakeside district of Dockside Village with festivals, food-tasting, and live entertainment right on the Intercoastal Waterway. There’s always good music at the House of Blues, magical rides on the waterfront carousel, and if you’re here for the holidays, the annual Lighting of the Landing tree-lighting ceremony takes place here around late November.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa2fdd02faeaffacae26_myrtle-aquarium.jpeg', 1, 1, 1, 34, 79, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Barker-Ewing Whitewater', 'barker-ewing-whitewater', '', '<p>One of the greatest thrills to experience in Jackson Hole is white water rafting.The river is safe and well suited for white water rafting, and Barker-Ewing has been doing this for a long time and is very experienced.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8faa6975f8133c1d2f39d_barker-ewing-whitewater.jpeg', 1, 1, 1, 43, 111, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Baron Bliss Lighthouse', 'baron-bliss-lighthouse', '', '<p>The Baron Bliss Lighthouse is one of the best places to experience the beautiful views of the entire city. The perfect times to visit this lighthouse are during sunrise or sunset. You will undoubtedly see spectacular views, which will make you want to go back every day. The lighthouse is an incredibly peaceful place to spend a few minutes, followed by a walk along the beach.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8faaf4ffa1625ad993cee_belize-lighthouse.jpeg', 1, 1, 1, 17, 88, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Barranquilla Beach', 'barranquilla-beach', '', '<p>Barranquilla Beach is one of the many stunning beaches within the city limits. The country of Colombia has a tropical climate year-round so that you can enjoy the beach any time you visit! Beach resorts dot the coast, offering many amenities, restaurants, and places to stay.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa9e0da8840cb6790c54_barranquilla-beach.jpeg', 1, 1, 1, 11, 75, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Barrio Antiguo', 'barrio-antiguo', '', '<p>While much of Monterrey is grounded firmly in the modern day, it also has a lively historic district that is well worth a visit. Stroll down the bright, colorful streets of the Barrio Antiguo (meaning “Old Neighborhood”) for an energetic nightlife of music and clubs.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa57b67dca4d44391375_monterrey-santa-lucia-riverwalk.jpeg', 1, 1, 1, 26, 100, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Barton Springs', 'barton-springs', '', '<p>If you need to cool down, one of the most popular attractions in Austin is the Barton Springs Pool. The 1,000-ft long, rectangular-shaped pool is lined with a paved sidewalk. The pool normally costs an entrance fee, but if you come during 5-8 am or 9-10 pm on alternating weekdays, or from the end of October to the end of February, there won’t be a charge.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8f9c7f877277281d6e76c_barton-springs.jpeg', 1, 1, 1, 30, 98, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Basilica de Suyapa', 'basilica-de-suyapa', '', '<p>The Basilica de Suyapa is the largest catholic church existing in Central America and home to Our Lady of Suyapa, the patroness of Honduras, statue. The statue of the Virgin Mary, mother of Jesus Christ, was uncovered in 1747, and thousands of individuals make a pilgrimage to view the sculpture and visit the Basilica de Suyapa each year.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa73b67dca44743913f2_tegucigalpa-basilica-de-suyapa.jpeg', 1, 1, 1, 14, 87, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Basilica of Saint Josaphat', 'basilica-of-saint-josaphat', '', '<p>This magnificent Franciscan basilica is historically significant to the city of Milwaukee; though technically finished in 1901, Polish parishioners had been using this place of worship since 1880. The church features a beautiful domed ceiling and spiral staircase and is one of the 82 minor basilicas in the U.S.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa524ffa169a1699332d_bdj.jpeg', 1, 1, 1, 43, 88, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Bats at Congress Bridge', 'bats-at-congress-bridge', '', '<p>At sunset on the Ann W. Richards Congress Avenue Bridge over the Colorado River, people gather to watch the world’s largest urban bat colony take flight from under the bridge out onto the lake and sky. It’s a seriously cool sight to see a swarm of bats this size, and from the bridge you can also appreciate a close-up view of the Colorado River. The best times to come are from late spring to early fall. It gets crowded quick, so come early!</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8f9d0c4030a1fc71470f3_11126441_10153854142033902_8314003723955811472_o-1000x615.jpeg', 1, 1, 1, 30, 98, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Batsfjord Church', 'batsfjord-church', '', '<p>Check out the Evangelical Lutheran church built in 1971. Its architectural style “long church” is considered modern and was designed by architect Hans Magnus.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8faa58f0aa258987e18af_batsfjord_church.jpeg', 1, 1, 1, 71, 30, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Bay Beach Amusement Park', 'bay-beach-amusement-park', '', '<p>The incredible park features rides, games, shows, and restaurants. This is a fun adventure for families and individuals of all ages.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8f9eaf62f259496275d3a_green-bay-amusement-park.jpeg', 1, 1, 1, 45, 88, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Beach and Boardwalk', 'beach-and-boardwalk', '', '<p>The boardwalk stretches for over 27 miles along the beautiful beaches of Panama City. It is the perfect place to take a leisurely walk or ride your bike while enjoying the stunning views.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa800da88460e3790b9b_panama-city-rainforest.jpeg', 1, 1, 1, 37, 122, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Beach Art Center', 'beach-art-center', '', '<p>The Beach Art Center is a great place to see the work of local artists. It features a rotating selection of exhibitions, as well as a gift shop. Admission is free for all visitors.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa7fa98ced096589ecef_panama-city-canal.jpeg', 1, 1, 1, 35, 90, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Beach Day', 'beach-day', '', '<p>You can’t go to Florida without spending some time on one of the beautiful sandy beaches that line nearly every shore-side city. Visit Madiera Beach to enjoy shops along the boardwalk or find yourself on an island in the sun. Enjoy a warm summer afternoon by catching some rays, or getting out into the shallows to enjoy some watersports on one of the many beaches whose waters only reach about 12 feet in depth.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8f9bde7a1f60ac588f6ab_pexels-riccardo-871060.jpeg', 1, 1, 1, 28, 82, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Beach Day', 'beach-day-4', '', '<p>El Salvador beaches are great for surfing, fishing, and just relaxing. Take a day and visit the coastal waters of San Luis Talpa. If you need some excitement, charter a boat or take a surfing class and learn how to ride the waves.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa5dcf6b6c41c5f01976_san-luis-beach.jpeg', 1, 1, 1, 13, 89, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Beaches', 'beaches', '', '<p>Mombasa is not short on seaside locales. Nyali Beach is the most famous of Mombasa’s many beaches for its white sand and recreational attractions. You’ll find hotels, restaurants, and even a crocodile farm, right here along the Kenyan coast. Pirates Beach is another frequented public beach, with shop vendors and henna artists that can give you lovely designs for a small fee. And last but not least; Diani Beach, one of Africa’s leading seaside destinations, is located just south of Mombasa in Kwale County.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8f9d9a98ced1f8f89db2d_Diani%2520Beach.jpeg', 1, 1, 1, 4, 40, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Beaches', 'beaches-2', '', '<p>Malindi is famous for its gorgeous beaches, and there are a few here that you can visit for free. Watamu Beach is a popular one, with a plethora of restaurants, hotels, and attractions close to Turtle Bay and the Marine Park. Che Shale Beach is open all year round and has a hotel right on the beachfront. You’ll find lots of coral gardens under the water at beaches in Malindi. The coral filters and cleans the water naturally, and their rocky barriers help protect the coastline from large tides.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa1bf87727de4bd6ecb9_1024px-che_shale_from_the_beach.jpeg', 1, 1, 1, 3, 40, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Beale Street', 'beale-street', '', '<p>Located in downtown Memphis, Beale Street is a historical landmark in Tennessee, and is a melting pot of jazz, rock ‘n’ roll, R&amp;B, and gospel music. Visitors can also find many restaurants and shops located on this iconic street.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8f9f2616f6d77c75820aa_memphis-beale-street.jpeg', 1, 1, 1, 35, 90, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Beale Street Landing & Memphis Riverboats', 'beale-street-landing-memphis-riverboats', '', '<p>The Beale Street Landing complex is the perfect destination for families. Located at the junction of Beale Street and the river, children can climb, slide and splash in the play area.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa2bdd02fa311cacadc0_memphis-riverboats.jpeg', 1, 1, 1, 35, 90, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Beau Vallon Beach', 'beau-vallon-beach', '', '<p>Consistently voted one of the most popular beaches in Africa, Beau Vallon Beach is a white-sand magnet to tourists and residents alike. The beach area has restaurants, concessions, and numerous modern hotels for tourists to enjoy.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8f9f9064c5a5501a62871_beau-vallon-beach.jpeg', 1, 1, 1, 5, 55, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Bellagio Conservatory and Botanical Garden', 'bellagio-conservatory-and-botanical-garden', '', '<p>Bellagio Conservatory &amp; Botanical Garden is certainly worth the visit. The sky-lit Bellagio atrium features vibrant, seasonal scenes composed of plants, flowers, and trees. Always meticulously planned and executed, it is the perfect spot for photos and selfies. Family friendly feature, the kids love it!</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa52f877276719d6eece_bellagio-conservatory-botanical-garden.jpeg', 1, 1, 1, 36, 115, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Belle Island Aquarium', 'belle-island-aquarium', '', '<p>This 1904 aquarium is not only the oldest in the nation, but also one of the most diverse. Featuring marine life around the globe, from Africa to the Great Lakes, this aquarium even owns the largest collection of air-breathing fish. This conservatory runs almost entirely on volunteers and donations, whichare appreciated at the Belle Island Aquarium but are not necessary.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8f9c7185e34150fb8a263_belle-isle-conservatory.jpeg', 1, 1, 1, 42, 83, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Bellver Castle', 'bellver-castle', '', '<p>Bellver Castle is a medieval fortress with a distinctive circle shape. It was built in the 14th century for King James II of Majorca, and is one of the few circular castles in Europe. Bellver Castle first served as the residence of the Kings of Majorca and then a military prison throughout the 18th to the mid-20th century. Come check out this unique building, walk around the exterior, and then explore the National History Museum inside the castle. Admission is free to Bellver Castle on Sundays from 10am-3pm.</p>', '', '', 1, 1, 1, 40, 3, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Benefit Street', 'benefit-street', '', '<p>Benefit Street is located in Old Providence and is known as the "History Mile." Benefit Street is closed to traffic and is a Pedestrian Mall. Homes and buildings on Benefit Street have been renovated and remodeled and now house shops, restaurants, bars, and nightclubs.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa60b67dca826b39139e_providence-benefir-street.jpeg', 1, 1, 1, 42, 71, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Benjamin Franklin Parkway', 'benjamin-franklin-parkway', '', '<p>The Benjamin Franklin Parkway was modeled after the famous Paris street, Avenue des Champs-Elysées. It is now home to many of Philadelphia’s museums and galleries.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8faabf4ed490ba81a1699_benjamin-franklin-parkway.jpeg', 1, 1, 1, 40, 75, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Bergenhus Fortress', 'bergenhus-fortress', '', '<p>The Bergen Fortress was built in the 10th century A.D. It was constructed to protect the city of Bergen and to give the settlers a place to go during times of danger. The restored fortress is now open to the public, along with a gift shop, museum, and restaurant.</p>', '', '', 1, 1, 1, 60, 5, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Berggarden', 'berggarden', '', '<p>Berggarden was a trading place back in 1878 that played an important role in developing Rorvik. There is a shop that stands from1910 that tells 150 years’ worth of Berggarden history.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa47185e34668ab8a701_rorvik_naeroysundet.jpeg', 1, 1, 1, 65, 11, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Bernard’s Beach', 'bernards-beach', '', '<p>One of the most popular beaches in Monrovia is Bernard’s Beach, and it is unspoiled and protected against future development. It is rated consistently as one of the best beaches in Africa.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa9df4ed499d011a1663_bernards-beach.jpeg', 1, 1, 1, 6, 11, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Bibler Gardens', 'bibler-gardens', '', '<p>Louis A. "Sam" Bibler designed the Bibler Gardens. He landscaped, planted, and maintained the gardens at his home from around 1979 until he died in 2002. The beautiful gardens, including ponds with swans, waterfalls, sculptures, a log stable and an arboretum, are landscaped today according to the desire of Sam to preserve the site as a place of longstanding beauty that could be enjoyed by the Kalispell community and visitors to the area.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa37f4ed49c6d31a0dcb_kalispell-bibler-garden.jpeg', 1, 1, 1, 48, 114, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Big Ben', 'big-ben', '', '<p>Big Ben is the nickname for the great bell of the striking clock at the north end of the Palace of Westminster in London. The name is frequently extended to refer to the clock and the clock tower. The clock and tower were built in 1843.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fab1f8772771d0d6f19c_big-ben.jpeg', 1, 1, 1, 52, 0, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Big Ben', 'big-ben-3', '', '<p id="">Big Ben is one of the most iconic landmarks in the United Kingdom. It is a must-see for everyone who visits London. Big Ben is located on the River Thames and is attached to the Houses of Parliament. Technically “Big Ben” actually refers to the bell inside of the clock tower, while the tower itself is named Elizabeth tower in honor of Queen Elizabeth II.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63cb17a15c439a85097cc70c_big-ben.jpeg', 1, 1, 11, 0, 0, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Boulders Beach', 'boulders-beach', '', '<p id="">Boulders Beach is a sheltered beach near Cape Town made up of inlets between large boulders, from which the name originated. It is a very popular beach, especially for tourists visiting Cape Town.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63cb17a18e226c0a98f13d7f_boulders-beach.jpeg', 5, 1, 5, 0, 0, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Buck Island', 'buck-island', '', '<p>Buck Island is located off the coast of St. Thomas and is a National Wildlife Refuge for birds, sea turtle nesting, and other native creatures. The only sign of modern civilization is the single structure on the island; a deserted lighthouse built in 1916.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fab0f62f2556a92766b7_st-thomas-buck-island.jpeg', 1, 1, 1, 18, 65, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
