INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Canalside', 'canalside', 'Come to Canalside at the old terminus of the Erie Canal, a large recreation area that was part of Buffalo’s waterfront redevelopment.', '<p>Come to Canalside at the old terminus of the Erie Canal, a large recreation area that was part of Buffalo’s waterfront redevelopment. Here at the bank of the canal there’s always something going on; fun (and mostly free) events are hosted throughout the year, and there’s an ice skating rink in the winter. You can play along the waterfront, find restaurants, shops, and live music.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fab6064c5a46f4a63069_canalside_buffalo_new_york_june_2015_-_04.jpeg', 1, 1, 1, 43, 79, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
