INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Catacombs', 'catacombs', 'The Catacombs of Paris is the underground city of the departed, a series of old quarries under the city of Paris.', '<p>The Catacombs of Paris is the underground city of the departed, a series of old quarries under the city of Paris. The bodies were relocated to the Catacombs in the nineteenth century when the cemeteries became overpopulated. The government led the reorganization of bones into unique walls and other arrangements, along with altars and columns. It is a very popular and unique attraction.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8f9eef4ed4901301a05c9_catacombs.jpeg', 4, 1, 4, 49, 2, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 3, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
