INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Cedar Ridge Preserve', 'cedar-ridge-preserve', '', '<p>The Cedar Ridge Preserve spans over 600 acres. It is a natural hiking spot that is popular among Dallas locals.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fab2f877271442d6f1c7_cedar-ridge-preserve.jpeg', 1, 1, 1, 33, 97, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Central Park', 'central-park', '', '<p>Central Park is an urban park in New York City located between the Upper West and Upper East sides of Manhattan. It is the largest park in the city by area, covering a whopping 843 acres. It features open fields of local wildlife, walking trails, twenty-one different playgrounds, an ice skating rink, and a large lake with paddle boats you can rent. Come see the changing of the leaves in the fall for a stunning Instagrammable experience.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa47b3a4cba494286dab_central-park.jpeg', 6, 1, 7, 41, 74, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 4, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Central Park', 'central-park-1', '', '<p id="">Central Park is an urban park in New York City located between the Upper West and Upper East sides of Manhattan.&nbsp; It is the largest park in the city by area covering 843 acres.&nbsp; It features open fields, walking trails, an amusement area, ice skating and a large lake with paddle boats.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63cb17a20760e770dc214910_new-york-central-park.jpeg', 6, 1, 7, 0, 0, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
