INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('CHATUCHACK MARKET', 'chatuchack-market', '', '<p id="">Chatuchak Market is the largest market in Thailand. Also known as JJ Market, it has over fifteen thousand stalls and employs twelve thousand vendors. Those who visit can find food, produce, clothing, arts, and crafts.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63cb17a2feacbc2d74f2eeaf_chatuchack-market.jpeg', 7, 1, 1, 0, 0, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Chelsea Market', 'chelsea-market', '', '<p>As soon as you get hungry, stop on over at Chelsea Market for free samples at a massive variety of food halls with cuisines from all over the world. Stroll past flower vendors and bookstores or browse vintage boutiques for unique jewelry. Spend all of your cash on quirky souvenirs at this Nabisco factory-turned shopping complex, or simply appreciate the delectable scent of fresh-baked pretzels and spicy tacos; all available at the picturesque Chelsea Market.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8faa9cd425370b7885335_chelsea-market.jpeg', 6, 1, 7, 41, 74, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 4, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Children’s Museum of Pheonix', 'childrens-museum-of-pheonix', '', '<p>Phoenix’s Children’s Museum is one of the top three largest Children’s Museums in the entire United States. There are over 300 interactive displays and exhibits for kids to have fun and learn about the world around them. The Children’s Museum is a fantastic place for learning, imagination, and creativity.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fab4c4030a248a148f54_phoenix-children-museum.jpeg', 1, 1, 1, 33, 112, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('ChocoMuseo', 'chocomuseo', '', '<p>The Dominican Republic exports more cacao than anywhere else in the world. Come to the chocolate museum for an overview of Dominican-exported cacao and try cacao-based food and drink samples for a taste of sweetness.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8faa33c5665cd696b6fe4_world-of-chocolate.jpeg', 1, 1, 1, 18, 70, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Cibola National Forest', 'cibola-national-forest', '', '<p>There is a peacefulness that comes from the beauty of the Cibola National Forest, and you will want to experience it. Each of the different walking trails throughout the forest lead to an astonishing view. From on high you can see some of the most breathtaking sights of the New Mexican landscape.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8faaf751715bee4c3e6b8_albuquerque-cibola.jpeg', 1, 1, 1, 35, 107, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('City Walls', 'city-walls', '', '<p>The City Walls art exhibit takes place all across the city and features full wall landscapes as well as smaller exhibits. These painted walls are popular photography spots, but also spruce up a scenic walk through the city.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8faa4b3a4cb2223287724_citywalls.jpeg', 1, 1, 1, 42, 83, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Clearwater Beach', 'clearwater-beach', '', '<p>Clearwater Beach is one of the world’s most popular and beautiful beaches. The beach is on a barrier island off the coast of Clearwater and is a wonderful place to enjoy family and friends.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fab3198c400a8e528670_st-petersburg-clearwater-beach.jpeg', 1, 1, 1, 28, 83, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Cleveland Lakefront Nature Preserve', 'cleveland-lakefront-nature-preserve', 'Settled on the banks of the Erie, this nature preserve grew out of literal pounds of garbage.', '<p>Settled on the banks of the Erie, this nature preserve grew out of literal pounds of garbage. Built on what was once a landfill, the clay and sediments of dumping throughout the 1970s began to sprout a diverse spread of fauna and flora. Today, the Cleveland Lakefront Nature Preserve includes 88 acres of shoreline, forests and meadows that have grown from nothing.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fab54ffa164e36993d08_listing-cle-lakefront.jpeg', 1, 1, 1, 42, 82, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Colonial Zone', 'colonial-zone', '', '<p>The Colonial Zone is a beautiful space that was issued the UNESCO heritage stamp of approval in 1990. Many buildings dating back to the 1600s live in this square. While it is a spot for tourists, it is also dearly loved by locals. Enjoy open-air markets in a lively space that has been compared to an outdoor museum.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fab14ffa16265d993cfa_colonialzone.jpeg', 1, 1, 1, 18, 70, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
