INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Cradle of Humankind', 'cradle-of-humankind', '', '<p>The paleoanthropological site is proclaimed the cradle of humankind. It was declared a World Heritage site by the U.N. in 1999. The site contains a series of archeological finds and several limestone caves.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8faace7a1f601be891be4_cradle-of-humankind.jpeg', 1, 1, 1, 26, 28, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Crocosaurus Cove', 'crocosaurus-cove', '', '<p id="">The humorlessly named<strong id=""> </strong>Crocosaurus Cove<strong id=""> </strong>is one of the best family-friendly spots you can visit in Darwin. It is a unique saltwater crocodile attraction where the crocs are kept behind secure cages. You can see a lot of their natural behavior and even feed the baby crocs! Marvel at the immense size of these beasts and think how they have been on the top of their food chain for millions of years. </p>', '', '', 8, 1, 8, 0, 0, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
