INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Dziana Dzaha', 'dziana-dzaha', '', '<p>Dziana Dzaha is a crater lake on the island of Pamazi in Mayotte. It is well known for its underwater lava tube caves and its fabled hidden pirate treasure.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8faa8a98ced6f3f89f14d_dzaoudzi-dziana.jpeg', 1, 1, 1, 13, 45, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Eiffel Tower', 'eiffel-tower', 'The Eiffel Tower is a beautiful wrought iron tower built in Paris overlooking the city.', '<p>The Eiffel Tower is a beautiful wrought iron tower built in Paris overlooking the city. When first constructed, the Eiffel was only to stand for 20 years and then be taken down. Many people were upset about the tower and protested to have it moved. Eventually, people changed how they felt, and 20 years later, when they were preparing to take the Eiffel Tower down, those same people fought to keep it.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa70975f814a05d2f1a4_eiffel-tower.jpeg', 4, 1, 4, 49, 2, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 3, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
