INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('El Museo del Caribe', 'el-museo-del-caribe', '', '<p>Take an interactive look at the best museum in town. Discover and learn about the indigenous cultures, nature, and history of this Caribbean coastal city at the Museum del Caribe. The interior of this building gives guests an insight into Barranquilla with displays that stretch over several different rooms and floors. There are artifacts, videos, and photos describing the biodiversity and history of the beautiful Caribbean, its people, and its culture. Guided tours are available.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fab33c56657e3c6b707e_barranquilla-el-museu.jpeg', 1, 1, 1, 11, 75, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Eureka Tower', 'eureka-tower', 'Currently the third tallest building in Australia - the other is the Q1 in Queensland.', '<p>Eureka Tower is a highly recommended attraction. This stunning skyscraper is nearly 300 meters high (or nearly 1,000 feet). Construction began in August 2002 and the exterior was completed in June 2006. Until 2019 it was the tallest building in Melbourne, beat by Australia 108, and is currently the third tallest building in Australia - the other is the Q1 in Queensland.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8faa1cd4253774088530e_1920x1080-Eureka%2520Tower.jpeg', 8, 1, 9, 38, 145, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 7, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Explore the Pub Scene', 'explore-the-pub-scene', '', '<p>A great way to get to know the city of Honningsvagis by going to some of the local pubs. Locals and fellow explorers alike tend to visit the unique pubs in the area. Each pub has its own original style and entertainment, you can even explore an artic bar!</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8faa6975f8155dcd2f39e_pexels-pixabay-159291-2.jpeg', 1, 1, 1, 44, 26, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Federal Hill Park', 'federal-hill-park', '', '<p>Overlooking the south shore of the Inner Harbor, this spacious park adds a splash of green to the cityscape. With a fantastic view of the downtown skyline, as well as an assortment of playgrounds, biking paths, and shady trees, this is an ideal stop for families and individuals alike to take a break - and a memorable photo!</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8faa6f8772728b8d6f163_baltimore-museum-art.jpeg', 1, 1, 1, 39, 77, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Fishing', 'fishing-3', '', '<p>Andenes offers a unique opportunity for fishing enthusiasts to fish in the sea and freshwater just a short distance from each other. Fishing trips in the winter months may include Northern Light sightings, ice fishing, and witnessing the migration of cod conducted annually.</p>', '', '', 1, 1, 1, 69, 16, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Flam Railway', 'flam-railway', '', '<p>Take a train ride on the Flam Railway. This railway climbs from sea level up to 2841 ft, which makes it the third steepest railway in the world. The views of mountains and waterfalls at the observation decks on top are breathtaking.</p>', '', '', 1, 1, 1, 61, 7, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Fort Barrancas', 'fort-barrancas', '', '<p>Visiting Fort Barrancas and learning about how the fort was part of sieges and government takeovers will be enthralling for those who enjoy history. Three countries in history have governed Fort Barrancas.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fab4f877274634d6f1d3_fort-barrancas.jpeg', 1, 1, 1, 30, 87, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Fort Union Trading Post', 'fort-union-trading-post', '', '<p>A top thing to do in the West and Williston, North Dakota, is peeling back layers of American history. Fort Union was a critical fur trading post from 1828 to 1867. Visitors to the present-day Fort Union Trading Post can tour the building and learn all about the history of the fur trade and its impact on expanding the United States.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8faaa198c4085145285b4_williston-trading-post.jpeg', 1, 1, 1, 48, 104, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Fullerton Arboretum', 'fullerton-arboretum', '', '<p>A fun, yet different stop along the way is the Fullerton Arboretum. It is free to the public and offers many programs to help both children and adults learn about the preservation of plant and animal life and showcase the natural habitat in which many species from around the world reside.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fab0fccaf95915cd4cb2_orange-fullerton.jpeg', 1, 1, 1, 34, 118, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Grand Central Terminal', 'grand-central-terminal', 'This historic world-famous landmark in Midtown Manhattan is not simply a transportation hub — it’s also a shopping, dining, and cultural destination all under one magnificent roof. Opened to the public in February 1913, Grand Central Terminal is a story of great engineering, survival, and rebirth. In 1978, architect Philip Johnson and First Lady Jacqueline Kennedy Onassis campaigned to secure landmark status for the Terminal, ensuring the building would serve New Yorkers for generations to come.', '<p id="">This historic world-famous landmark in Midtown Manhattan is not simply a transportation hub — it’s also a shopping, dining, and cultural destination all under one magnificent roof. Opened to the public in February 1913, Grand Central Terminal is a story of great engineering, survival, and rebirth. In 1978, architect Philip Johnson and First Lady Jacqueline Kennedy Onassis campaigned to secure landmark status for the Terminal, ensuring the building would serve New Yorkers for generations to come.</p><p id="">Grand Central Terminal is one of the country’s great architectural achievements and New York City’s unofficial meeting place with thousands of people choosing to “meet me at the clock” –rendezvousing with friends and loved ones each day at the opal-faced Main Concourse Information Booth Clock. Hailed as a temple to the everyday commuter, this cathedral-like building was constructed to honor you, its visitors.</p>', 'https://s3.us-west-2.amazonaws.com/assets.exploro.com/demo/Grand+Central+Station+Hyperlapse+-+New+York+City+POV+-+4K.mp4', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63cc7fc5716c4982021c1a63_GrandCentralTerminal-2020-GettyImages-88853326.webp', 6, 1, 7, 0, 0, 'info@grandcentral.com', '(212) 340-2583', '89 E 42nd St, New York, NY 10017', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 4, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Grindsted Engso', 'grindsted-engso', '', '<p>Grindsted Engso is an artificial lake created in 1971-1972 as a relaxing recreational landscape for locals and visitors. The lake area is popular with locals. It includes hiking, biking, and running trails around Grindsted Engso. There are ample fishing opportunities, but the lake is catch and release and requires a fishing permit. The Lake is a beautiful place to spend the afternoon.</p>', '', '', 1, 1, 1, 56, 9, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Halbrendsfossen', 'halbrendsfossen', '', '<p>We recommend visiting in the spring to enjoy this favorite tourist hotspot. The waterfall is 60 meters high and about 2.5 miles from the center of Forde.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fab1064c5a5d95a63056_norway-falls.jpeg', 1, 1, 1, 61, 6, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Heineken Experience', 'heineken-experience', '', '<p id="">The Heineken Experience is an interactive museum where the visitor can witness the brewing of beer, as well as taste brewed beer ready for bottling.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63cb17a3ea97be775ab49739_heineken-experience.jpeg', 3, 1, 1, 0, 0, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Humayun’s Tomb', 'humayuns-tomb-6', 'In 1569, Humayun’s first wife, Empress Bega Begum (also known as Haji Begum), commissioned Humayun’s Tomb for Mughal Emperor Humayun.', '<p>In 1569, Humayun’s first wife, Empress Bega Begum (also known as Haji Begum), commissioned Humayun’s Tomb for Mughal Emperor Humayun. Persian architects Mirak Mirza Ghiyas and his son, Sayyid Muhammad, were chosen by the Empress to design the tomb.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8f9eb751715b232c3d247_delhi-humayuns-tomb.jpeg', 9, 1, 10, 29, 77, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 5, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('If You Visit In The Fall, Take Some Time To See The Leaves Change', 'if-you-visit-in-the-fall-take-some-time-to-see-the-leaves-change', '', '<p>The leaves change every year in the fall, and the roads are filled with people who come to see them. Forests surround Burlington, and you can see the beautiful changing of the leaves anywhere you go.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8faa2b3a4cba01d2876fd_burlington-visit-fall.jpeg', 1, 1, 1, 44, 73, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Ilamatepec Volcano', 'ilamatepec-volcano', '', '<p>The Ilamatepec Volcano, also known as the Santa Ana Volcano, is just a short drive from the La Paz district and San Luis Talpa. The site includes four volcanic calderas, one filled with crystal clear water.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8faa784a160dc9d194a15_san-luis-volcano.jpeg', 1, 1, 1, 13, 89, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('India Gate', 'india-gate', 'The All India War Memorial is now known as India Gate. You can find it on the eastern edge of the "ceremonial axis" of New Delhi.', '<p>The All India War Memorial is now known as India Gate. You can find it on the eastern edge of the "ceremonial axis" of New Delhi, which was once called the Kingsway. It is a memorial to the 70,000 British Indian soldiers who lost their lives between 1914-1921 in the First World War and the Second Anglo-Afghan War.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8f9fe7517159695c3d333_delhi-india-gate.jpeg', 2, 1, 10, 29, 77, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 2, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Jack London Square', 'jack-london-square', 'Famed author Jack London, who wrote White Fang and Call of the Wild, claimed Oakland as his hometown where he grew up and wrote some of his novels.', '<p>Famed author Jack London, who wrote White Fang and Call of the Wild, claimed Oakland as his hometown where he grew up and wrote some of his novels. Now, the neighborhood in which he lived is a popular entertainment square and marina to spend your nights out on the town. Bars, artisan eateries, music spilling out of shops, a train station and even a saloon: the square combines a historic neighborhood port with fun and color.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fab684a160a0d0194cd7_jack-london-square-cover.webp', 1, 1, 1, 38, 122, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Jackson Hole Historical Society and Museum', 'jackson-hole-historical-society-and-museum', '', '<p>The museum focuses on Jackson Hole’s rich history and the founding and development of Yellowstone National Park and Grand Teton National Park.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8faa309d76dbf6932120c_jackson-hole-historical-society-museum.jpeg', 1, 1, 1, 43, 111, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Jama Masjid of Delhi', 'jama-masjid-of-delhi', 'The Masjid-i Jehan-Numa is commonly known as the Jama Masjid of Delhi and is one of the largest mosques in the country.', '<p>The Masjid-i Jehan-Numa is commonly known as the Jama Masjid of Delhi and is one of the largest mosques in the country. It is a beautiful building constructed of white marble and red sandstone. The Mughal Emperor Shah Jahan started building the mosque in 1650 and finished it in 1656, costing over one million Rupees. The courtyard is vast and accommodates 25,000 people.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa288f0aa236287e0dcc_delhi-jama-masjid.jpeg', 9, 1, 10, 29, 77, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 5, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Jean-Jacques Dessalines Statue', 'jean-jacques-dessalines-statue', '', '<p>Jean-Jacques Dessalines joined the slave rebellion in 1791. He rose rapidly through the army ranks to become a senior Lieutenant and infamously defended an important fort for 20 days against French forces. Visitors to the monument will learn about this figure and his contribution to Haitian history.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8faa409d76d2a11321217_cap-haitien-jean-jaques.jpeg', 1, 1, 1, 20, 72, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Jebel Ali Racecourse', 'jebel-ali-racecourse', 'Jebel Ali Racecourse is a horse racing track located in the city of Jebel Ali, Dubai, United Arab Emirates. The racecourse was opened in 1989 and is operated by the Emirates Racing Authority. The track is known for its unique layout, featuring a long straightaway and tight turns, making for an exciting and challenging race for both horse and jockey.', '<p>Jebel Ali Racecourse is a horse racing track located in the city of Jebel Ali, Dubai, United Arab Emirates. The racecourse was opened in 1989 and is operated by the Emirates Racing Authority. The track is known for its unique layout, featuring a long straightaway and tight turns, making for an exciting and challenging race for both horse and jockey.</p>', 'https://s3.us-west-2.amazonaws.com/assets.exploro.com/demo/Race+%237+-+9_12_22+-+Jebel+Ali+Free+Zone+(Jafza).mp4', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63ccbb89aaf20a2c92df9551_Jebel%20Ali%20Racecourse.jpg', 2, 1, 2, 0, 0, 'info@racebourse.com', '+971 4 347 4914', 'Dubai-Abu Dhabi Road, Beside Emirates Golf Club - Dubai - United Arab Emirates', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 2, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Jumeriah Mosque', 'jumeriah-mosque', 'Jumeirah Mosque is a historic mosque located in the Jumeirah district of Dubai, United Arab Emirates. It is one of the few mosques in the country that is open to non-Muslims for tours and visits. The mosque is notable for its architectural style, which combines traditional Islamic design elements with modern influences.', '<p id="">Jumeirah Mosque is a historic mosque located in the Jumeirah district of Dubai, United Arab Emirates. It is one of the few mosques in the country that is open to non-Muslims for tours and visits. The mosque is notable for its architectural style, which combines traditional Islamic design elements with modern influences.</p>', 'https://s3.us-west-2.amazonaws.com/assets.exploro.com/demo/Coffee+Morning+with+Just+Kidding+at+Jumeirah+Mosque.mp4', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63cc8b7084e7a26ed0bf6dcc_Jumeirah%20Mosque.jpg', 2, 1, 2, 0, 0, 'info@jumeriah.com', '+971 4 353 6666', 'Jumeirah Beach Road - Jumeirah 1 - Dubai - United Arab Emirates', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 2, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
