INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Kristiansand Cathedral', 'kristiansand-cathedral', '', '<p>The 17th-century Neo-Gothic Catholic Cathedral was rebuilt twice due to fire damage. In order to preserve the surviving walls of the church, the altar was oriented to the west instead of the east which mirrors what most churches look like today. In the summer there are free guided tours and organ concerts.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8faa75ee8113b71aacf10_2757230292_258ca2f013_k.jpeg', 1, 1, 1, 58, 8, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Lake Retba', 'lake-retba', '', '<p>Lake Retba is a salt lake just outside of Dakar known for its pink color due to the lake’s high algae content and salt content. Residents will go and float in the lake for medicinal reasons.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8faaeb67dca5cd1391559_lake-retba.jpeg', 1, 1, 1, 15, 17, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Leake Street Graffiti Tunnel', 'leake-street-graffiti-tunnel', '', '<p id="">London happens to have several legal graffiti tunnels, with Leake Street Graffiti Tunnel being the largest. It is considered to be one of the coolest street art spots in the entire city. It was transformed from an old railway tunnel into an art exhibit back in 2008 by the world-renowned, anonymous artist Banksy. Since then, the tunnel has been painted over and over again by many talented street artists. Visit the tunnel and see if you can catch one of the artists at work.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63cb17a3fbf4f556aa03468a_leake_street_graffiti_tunnel_looking_north.jpeg', 1, 1, 11, 0, 0, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Lidaveten', 'lidaveten', '', '<p>Lidaveten is a beautiful hike that is around 2.8 miles long. It takes a little less than two and a half hours to complete and is considered to be a moderately challenging hike. This trail is a great place for people who enjoy bird-watching as well as taking in the natural flora and fauna of Norway.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8faa78f0aa29af87e18b6_1024px-lidaveten.jpeg', 1, 1, 1, 62, 6, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Lombard Street', 'lombard-street', '', '<p>Lombard Street is one of the most beautiful areas in San Francisco to visit. The street is known for its steep, one-block section with eight sharp turns. Gorgeous plants and natural scenery surround the winding road, known as "the most crooked street in the world." Over two million visitors visit Lombard Street per year.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8faac0da8846a8b790cd8_lombard.jpeg', 1, 1, 1, 38, 122, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Louvre Museum', 'louvre-museum', 'The Louvre has an extensive collection, including the Mona Lisa.', '<p>The Louvre has an extensive collection, including the Mona Lisa. It is the most visited museum in the world.</p>', 'https://demo.exploro.com/3rdparty/gde/videos/Paris_Louvre%20museum%20-%20History%20-60.mp4.mp4', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8f9e33c5665ee866b55a2_louvre-museum.jpeg', 4, 1, 4, 49, 2, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 3, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
