INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('LUMPHINI PARK', 'lumphini-park', '', '<p id="">Lumphini Park is the largest park in Bangkok. Within the crowded confines of Bangkok, it provides much needed public space. The park features natural vegetation, a lake with boat rentals, and numerous walking trails.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63cb17a3ada3653c54b71a21_lumphini-park.jpeg', 7, 1, 1, 0, 0, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Metropolitan Museum of Art', 'metropolitan-museum-of-art', 'The Met presents over 5,000 years of art from around the world for everyone to experience and enjoy.', '<p id="">The Metropolitan Museum of Art presents over 5,000 years of art from around the world for everyone to experience and enjoy. The Museum lives in two iconic sites in New York City—The Met Fifth Avenue and The Met Cloisters. Millions of people also take part in The Met experience online.</p><p id="">Since its founding in 1870, The Met has always aspired to be more than a treasury of rare and beautiful objects. Every day, art comes alive in the Museum''s galleries and through its exhibitions and events, revealing new ideas and unexpected connections across time and across cultures.</p><p>‍</p>', 'https://s3.us-west-2.amazonaws.com/assets.exploro.com/demo/MET+museum+(NYC+4k).mp4', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63cc81dad67b5cc0f927e253_atm_teaser_700x440.jpeg', 6, 1, 7, 0, 0, '', '', '1000 5th Ave, New York, NY 10028', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 4, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Miller Outdoor Theater', 'miller-outdoor-theater', '', '<p>This outdoor theater space is great to see live orchestral performances, dance showcases, magic shows, and classical choral concerts. The theater can also be rented out for private events. Each production is posted on YouTube for 48 hours after the performance if you happen to miss the show.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8faa7185e34e93ab8ad68_miller-theater.jpeg', 1, 1, 1, 30, 95, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Mindil Beach', 'mindil-beach', '', '<p id="">Mindil Beach<strong id=""> </strong>is home to some of the most amazing sunsets in all the world. The beach is within walking distance of downtown, located near Darwin''s central business district. The beach is home to the Mindil Beach Sunset Markets during the dry season. They are a popular attraction and host thousands of people.</p>', '', '', 8, 1, 8, 0, 0, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Mo I Rana Caves', 'mo-i-rana-caves', '', '<p>Mo I Rana is famous for its multiple cave systems that you can explore in both summer and winter. Two caves are now open to the public. The Gronligrotta cave is lit, formed from limestone over thousands of years, and has a network of passages that are accessible only with a guide. If you are into adventures, the Setergrotta Cave is one of the largest caves in Norway and has marble passageways and even an underground river!</p>', '', '', 1, 1, 1, 66, 14, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Atlanta Monetary Museum', 'monetary-museum', '', '<p>The Atlanta Monetary Museum is also free. Historical artifacts and interactive multimedia exhibits are fun for the kids. Check out where millions of U.S. dollars are counted, sorted, and shredded. You can also receive a group tour of the vault and see the robots that are responsible for most of the production process.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8f9d0064c5a666da6240a_aa64318d-6f35-465d-827a-cf45f67c804etmb.png', 1, 1, 1, 34, 84, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
