INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Old Town San Diego', 'old-town-san-diego', '', '<p>San Diego was settled in 1769 in the area of Old Town, now restored to its vintage look. Old Town is the place you want to visit to learn the exciting history of San Diego and its people. See the oldest schoolhouse in San Diego, stop at the blacksmith’s shop, and wander around the historic Heritage Park. Pay a visit to the Junipero Serra Museum to learn more about the first settlement in California right where it stands.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8faa67517151ccfc3e515_old-town-san-diego.jpeg', 1, 1, 1, 33, 117, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
