INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Pioneer Air Museum', 'pioneer-air-museum', '', '<p>Alaska is so large, and the cities so far apart that civilization would not be possible without airplanes. The Pioneer Air Museum focuses on how airplanes have affected the culture of Alaska. The museum has many aircraft that were donated.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8faa8fccaf9e901cd4bab_fairbanks-air-museum.jpeg', 1, 1, 1, 65, 148, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Pittsburgh Farmer’s Markets', 'pittsburgh-farmers-markets', '', '<p>If you arrive in Pittsburgh in the warm weather months, you will want to be sure you stop by one of the many farmer’s markets found almost daily in this city that loves its food. You can find eggs, produce, cheese, baked goods, honey, Amish furniture and rugs, and a number of other items that Pittsburgh natives love.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8faaa3c56651ba16b7042_farmers_markets_pittsburgh_region_2.jpeg', 1, 1, 1, 40, 80, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Place Massena', 'place-massena', '', '<p>The Place Massena is one of Nice’s busiest spots for hangouts and celebration. The Place Massena separates the Avenue Jean Médecin and Vieux-Nice, and has a large open area with sculptures, shops, and cafes. You’ll recognize the square by the black-and-white checkered grounds, tying together Old Town architecture with contemporary upscale shopping and popular retail chains.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fab3064c5a0282a63062_5051950281_da51b6aef2_c_0.jpeg', 1, 1, 1, 44, 7, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
