INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Powell’s Books', 'powells-books', '', '<p>Powell’s Books is the largest independently owned bookstore in the world. The bookstore has thousands of books in its three-story building, and it features one of the world’s largest children’s book collections.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8faa4cf6b6cb754f01bc0_portland-oregon-powells-books.jpeg', 1, 1, 1, 46, 123, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Pulpit Rock', 'pulpit-rock', '', '<p>Pulpit Rock is a high hill overlooking Lysefjord with a sheer cliff drop-off. It is known worldwide as an excellent spot for base jumping. Pulpit rock has always been fairly well known but reached greater heights of fame when it was featured in the movie Mission: Impossible- Fallout. To reach Pulpit rock you have to hike along the Preikestolen trail. The trail is five miles round trip. But once you reach the cliff you have an amazing view of the city around 2,000 feet below you.</p>', '', '', 1, 1, 1, 59, 6, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Punta Gorda Nature Park', 'punta-gorda-nature-park', 'This park is another excellent way to spend the afternoon if you enjoy walking on nature paths and exploring the outdoors.', '<p>This park is another excellent way to spend the afternoon if you enjoy walking on nature paths and exploring the outdoors. This park hosts 21 acres of unique wildlife, lush green forests, exciting marsh areas, long hiking trails, scenic picnic tables, and a stable fishing pier. Explore this thrilling and expansive nature preserve in the beautiful Florida weather.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fab5f4ed4932a11a1721_punta-gorda-nature-park.jpeg', 1, 1, 1, 27, 82, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Ravnedalen', 'ravnedalen', '', '<p>Ravnedalen Park, known for its romantic setting, offers explorers many trails, lakes, and secluded nature spots to enjoy.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8faad064c5aef45a63042_v.jpeg', 1, 1, 1, 58, 8, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Rembrandt House Museum', 'rembrandt-house-museum', '', '<p id="">The Dutch Master’s home and studio is a living museum of Rembrandt’s life and a collection of his works of art.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63cb17a4d802dfe55866d6c7_amsterdam-rembrandt-house-museum.jpeg', 3, 1, 1, 0, 0, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Robben Island', 'robben-island', '', '<p id="">Robben Island is an island in Table Bay that is a living museum. It was first developed as a leper colony and then became a prison during the Apartheid Era for those who opposed Apartheid. Nelson Mandela, later president of South Africa, was once a prisoner on Robben Island.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63cb17a45bb3ee84a740d404_robben-island.jpeg', 5, 1, 5, 0, 0, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Rodadero Beach', 'rodadero-beach', '', '<p>Rodadero is a community in Santa Marta and home to many resorts, restaurants, bars, and nightclubs.The beach is one of the most visited places in the area, with various activities available to all who visit.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fab3185e34795ab8ae59_rodadero-beach_0.jpeg', 1, 1, 1, 11, 74, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Royal Botanic Gardens Victoria', 'royal-botanic-gardens-victoria', '', '<p>Royal Botanic Gardens Victoria or more simply known as the Melbourne Gardens, is a 336-hectare botanic garden founded in 1846. The beautiful landscape is full of both Australian and non-native species of plants. A truly stunning display of plant life complete with a beautiful river carving its way through the terrain. There are nearly 50,000 individual plants representing 8,500 different species displayed in 30 living plant collections.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa445ee8117168aacaf5_1920x1080-Royal%2520Botanic%2520Gardens%2520Victoria.jpeg', 8, 1, 9, 38, 145, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 7, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Saint Matthews Cathedral', 'saint-matthews-cathedral', '', '<p>The Saint Matthews Cathedral in Khartoum is the largest Christian place of worship in the city. The cathedral is located on the banks of the Blue Nile overlooking the city of Khartoum.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8faa5dd02fa2e17acb6e5_saint-matthews-cathedral.jpeg', 1, 1, 1, 16, 33, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('San Xavier Del Bac Mission', 'san-xavier-del-bac-mission', '', '<p>This historic mission church was built in the early 1700s and is still used as a church today. The mission has been completely restored and is an excellent example of early Arizona construction.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fab4185e34062bb8ae79_tucson-san-xavier-del-bac-mission.jpeg', 1, 1, 1, 32, 111, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Santa María Cathedral of Palma', 'santa-maria-cathedral-of-palma', '', '<p>The Cathedral of Santa Maria of Palma (Cathedral of St. Mary of Palma), more commonly referred to as La Seu (a title also used by many other churches), is a Gothic Roman Catholic cathedral.This impressively massive Gothic landmark began construction in the 13th century and underwent several industrial changes and cosmetic upgrades. It overlooks the Bay of Palma and gives an impressive view over the water. There is a fee to enter, but it is definitely worth it to just wander around the outside and take pictures. It is truly a unique and beautiful building and shouldn’t be missed!</p>', '', '', 1, 1, 1, 40, 3, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('See the Northern Lights', 'see-the-northern-lights-7', '', '<p>The Northern Lights are one of the seven natural wonders of the world. This natural phenomenon is known by several names, such as the Aurora Borealis, or simply just Aurora. These lights are a must-see for anyone who comes to visit Northern Norway. While you can see the lights year-round, November to February is usually the best time to visit, since this season is when Norwegian nights are the longest.</p>', '', '', 1, 1, 1, 70, 19, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Sjunkhatten National Park', 'sjunkhatten-national-park', '', '<p>The Sjunkhatten National Park is the largest national park in Norway. It provides opportunities for boating, skiing, and hiking. There are several well-defined walking trails, overlooks, and observation areas for the outdoor enthusiast.</p>', '', '', 1, 1, 1, 67, 15, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Ski Dubai', 'ski-dubai', 'Ski Dubai is an indoor ski resort featuring skiing, ice skating, and other winter sports all in a desert city.', '<p>Ski Dubai is an indoor ski resort featuring skiing, ice skating, and other winter sports all in a desert city. The temperature inside remains a constant 32 degrees and the resort covers 22,500 square meters of space making it one of the largest indoor ski resorts.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8f9e5975f816f46d2e5bf_ski-dubai.jpeg', 2, 1, 6, 25, 55, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 2, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('South African Museum', 'south-african-museum', '', '<p id="">The South African Museum is dedicated to the history and culture of South Africa. In addition to modern displays of recent history are fossils and archeological exhibits of the ancient history of the area.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63cb17a51fe6f5426f41abf6_south-african-museum.jpeg', 5, 1, 5, 0, 0, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Stroll through La Candelaria', 'stroll-through-la-candelaria', '', '<p>Explore the Old Town of La Candelaria, with buildings that are hundreds of years old. The streets are narrow and cobblestoned, and most of the original homes and buildings have been renovated into cafes, shops, or nightclubs. La Candelaria has a modern twist, while still regaining the ancient charm of its history.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8faa5064c5a8af5a63001_la-candelaria.jpeg', 1, 1, 1, 5, 74, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Tawau Hills Park', 'tawau-hills-park', '', '<p>Tawau Hills Park was first established in 1979 to provide protection for the water catchment area of Tawau town. It is located just 24 kilometers from Tawau, and comprises 279.72 km² of lowland dipterocarp rainforest, encompassed by oil palm and cacao plantations. The park has picnic areas, camp sites, and chalets (like a cabin). There are many great things in the park such as a hot spring and several spectacular waterfalls.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8faaef4ed4905391a1704_tawau-hills-.jpeg', 1, 1, 1, 5, 118, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Tetrapod Monument', 'tetrapod-monument', '', '<p>The Tetrapod Monument was erected, approved and built byKing Olav, King Harald, and Queen Sonja. The monument symbolizes the breakwater that creates a barrier between the land and sea and is very important to the city.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8faa2b3a4cbf6f42876f6_1024px-dwarka_-_lighthouse.jpeg', 1, 1, 1, 71, 29, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('The British Museum', 'the-british-museum', '', '<p id="">Did you know that most London Museums offer free admission? (Though they do accept donations!) The British Museum happens to be one of the largest museums in the country. You could spend several days wandering around the museum and still have more to see. It has a wide variety of artworks and artifacts on display and frequently brings in new exhibits for the public to view.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63cb17a6d7632e36202bb4bd_british-museum.jpeg', 11, 1, 11, 0, 0, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 3, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('The Brooklyn Bridge', 'the-brooklyn-bridge-3', '', '<p id="">The Brooklyn Bridge is a cable/suspension bridge in New York spanning the East River between the boroughs of Manhattan and Brooklyn. &nbsp;The bridge opened in 1883, and was the first fixed crossing in New York over the East River.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63cb17a63f5ddf0db86e2adf_new-york-brooklyn-bridge.jpeg', 6, 1, 7, 0, 0, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('The Burj Khalifa', 'the-burj-khalifa', '', '<p id="">The Burj Khalifa is a skyscraper in the city of Dubai.&nbsp; With a total height of 830 meters, it is the tallest building in the world.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa58064c5a21d2a62d05_burj-khalifa.jpeg', 2, 1, 2, 0, 0, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('The Cape of Good Hope', 'the-cape-of-good-hope', '', '<p id="">The Cape of Good Hope is just south of Cape Town. It is the southern tip of Africa and is the division point between the Atlantic and the Indian Oceans.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63cb17a6fc84bbb8a8d19688_the-cape-of-good-hope.jpeg', 5, 1, 5, 0, 0, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('The Cathedral of Notre-Dame', 'the-cathedral-of-notre-dame', 'Notre Dame is a 12th-century cathedral in Paris. It is one of the most visited cathedrals in the world.', '<p>Notre Dame is a 12th-century cathedral in Paris. It is one of the most visited cathedrals in the world. A few years ago, this famous tourist destination caught on fire, and the cathedral is currently closed while the burned areas of the church are repaired. The work is ahead of schedule, and the cathedral should reopen in late 2022.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa4a0da884fda879084f_notre-dame-de-paris.jpeg', 4, 1, 4, 49, 2, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 3, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
