INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('The Church Street Marketplace', 'the-church-street-marketplace', '', '<p>The Church Street Marketplace, located in the older part of Burlington, is now a pedestrian shopping area with restaurants, shops, crafts, and nightclubs. It is not unusual to spend a whole day visiting the marketplace.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8faaaf877273f6dd6f183_burlington-streetmarket.jpeg', 1, 1, 1, 44, 73, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('The Darwin Military Museum', 'the-darwin-military-museum', '', '<p id="">The Darwin Military Museum<strong id=""> </strong>has an extensive collection of items from the war, including Navy, Army, and Air Force items from Australian, US, and other armed forces. Its area was one of the most heavily fortified parts of Australia and at one time had over 110,000 armed forces personnel. General Douglas MacArthur’s campaign to liberate Manila was launched from this base. </p>', '', '', 8, 1, 8, 0, 0, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('The Downtown Historical Center', 'the-downtown-historical-center', '', '<p>UNESCO declared the Downtown Historical Center of Florence a World Heritage Site in 1982. The city is known for its culture: and hosts beautiful Renaissance art, architecture, and monuments, as well as many museums and art galleries in the Center. Be sure to check out Uffizi Gallery and the Palazzo Pitti. Forbes has even ranked Florence and specifically mentioned the Historical Center as one of the most beautiful cities in the world.</p>', '', '', 1, 1, 1, 19, 99, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('The Dubai Fountain', 'the-dubai-fountain', 'The Dubai Fountain is a choreographed fountain system located on a 12-hectare man-made lake in the center of Dubai’s rapidly developing central core.', '<p>The Dubai Fountain is a choreographed fountain system located on a 12-hectare man-made lake in the center of Dubai’s rapidly developing central core.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8faabc4030aa4df148f36_the-dubai-fountain.jpeg', 2, 1, 6, 25, 55, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 2, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('The Empire State Building', 'the-empire-state-building', '', '<p id="">The Empire State Building is a 102-story Art Deco skyscraper in mid-town Manhattan.&nbsp; It was completed in 1931 and its observation deck offers spectacular views of the city and remains a top tourist destination.&nbsp; It is named after the nickname for the state of New York, the ‘Empire State’.</p>', 'https://s3.us-west-2.amazonaws.com/assets.exploro.com/demo/Empire+State+Building+NYC+Fireworks+Timelapse+2020+4K.mp4', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63cb17a80949d976720ef079_new-york-empire-state-building.jpeg', 6, 1, 7, 0, 0, 'info@newyork.com', '(212) 736-3100', '20 W 34th St., New York, NY 10001', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('The Giraffe Center', 'the-giraffe-center', '', '<p>The Giraffe Center was established to protect the endangered giraffe, found only in the grasslands of East Africa. As described by one review: “Well maintained place and Giraffes are trained well. They just snatch the edible pellets by tongue precisely. Giraffes are gentle and well named as gentle giants. Very good experience”.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8faa664b86c14803bda90_The%2520Giraffe%2520Center2.jpeg', 1, 1, 1, 1, 37, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('The Golden Triangle', 'the-golden-triangle', '', '<p>The complex comprises Bukit Bintang, KLCC Park, (think walking paths, water fountain and wading pool) and Chinatown (Pedaling S). It is also the main shopping and restaurant hub of the city! Notably, they have 19 stores and plazas that boast impressive materials and items that any shopper would be pleased to have in their collection. With frequent sales events going on in this active shopping hub, you can be sure to find well priced items and fabrics that even the locals take delight in.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8faa55ee8113d0daacf0d_golden-triangle.jpeg', 1, 1, 1, 3, 102, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('The Grand Palace', 'the-grand-palace', '', '<p id="">The Grand Palace is a complex with several buildings located near the center of Bangkok. The palace has been the home of the kings of Siam since the nineteenth century. Famed for its beauty, it is considered one of the most opulent royal palaces in the world.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63cb17a80949d9697c0ef077_thegrandpalace.jpeg', 7, 1, 1, 0, 0, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('The London Eye', 'the-london-eye', '', '<p id="">The London Eye, or the Millennium Wheel, is a cantilevered observation wheel on the South Bank of the River Thames in London that offers an impressive view of the city.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63cb17a85c439a56eb7cc796_london-eye.jpeg', 11, 1, 11, 0, 0, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 3, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('The Metropolitan Museum of Art', 'the-metropolitan-museum-of-art', '', '<p id="">The Metropolitan Museum of Art, nicknamed ''The Met,'' is the largest art museum in the United States. With over 7 million visitors each year, it is the third most visited museum in the world.</p>', 'https://s3.us-west-2.amazonaws.com/assets.exploro.com/demo/MET+museum+(NYC+4k).mp4', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63cb17a8fbf4f55e4d034752_the-metropolitan-museum-of-art.jpeg', 6, 1, 7, 0, 0, 'info@museum.com', '(212) 535-7710', '1000 5th Ave, New York, NY 10028', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 4, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('The Miri City Fan Recreation Park', 'the-miri-city-fan-recreation-park', '', '<p>The Miri City Fan Recreation Park is another closer option for recreation. It is a large public space with many different activities. The most popular being jogging and exercising in the park. It is also a great place to just hang out and soak in the surroundings, especially at nighttime.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8faa3c4030a40ff148e52_miri-city.jpeg', 1, 1, 1, 4, 114, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('The Mountain National Park', 'the-mountain-national-park', '', '<p id="">The Table Mountain National Park surrounds Table Mountain which stands over Cape Town. The mountain’s 1,000-meter summit affords sweeping views of Cape Town and Table Bay, and is reachable by cable car, car, bike or on foot. The park is also among the world’s richest floral region.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63cb17a80949d94e8c0ef081_the-mountain-national-park.jpeg', 5, 1, 5, 0, 0, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('The Museum and Art Gallery of the Northern Territory', 'the-museum-and-art-gallery-of-the-northern-territory', '', '<p id="">The Museum and Art Gallery of the Northern Territory is also known as the MAGNT. It is a wonderful place to soak in the culture. Each year the MAGNT presents internally developed exhibits and traveling exhibits from all around Australia. In addition to bringing these great exhibits to life, it is also the home of Australia''s longest-running set of awards for all the Indigenous Australian artists. </p>', '', '', 8, 1, 8, 0, 0, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('The National Gallery of Victoria', 'the-national-gallery-of-victoria', 'one of the most extensive and interesting collections of art in all the world.', '<p>The National Gallery of Victoria, popularly known as the NGV, is an art museum in downtown Melbourne. It was first founded in 1861 and is Australia’s oldest and most visited art museum. It is seen by many as being one of the most extensive and interesting collections of art in all the world.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa5364b86c225f3bd2c0_1920x1080-The%2520National%2520Gallery%2520of%2520Victoria.jpeg', 8, 1, 9, 38, 145, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 7, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('The National Maritime Museum', 'the-national-maritime-museum', '', '<p id="">This museum focuses on Amsterdam’s long partnership with the North Sea.&nbsp;&nbsp; One exhibit is the growth of ships and shipbuilding in Amsterdam.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63cb17a957b94efdccbe66d4_amsterdam-maritime-museum.jpeg', 3, 1, 1, 0, 0, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('The National Sports Museum', 'the-national-sports-museum', '', '<p>The National Sports Museum is a unique and interesting museum dedicated to Australian sports and athletes. It is located within the Melbourne Cricket Ground. Cricket is by far the most popular sport featured at the museum, and indeed in the lives of the citizens, most of whom are avid fans. The museum features artifacts and interactive displays tracing the history of Australian sport.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa3bdd02faacc6acb00b_1920x1080-The%2520National%2520Sports%2520Museum.jpeg', 8, 1, 9, 38, 145, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 7, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
