INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('The Wild Wadi Waterpark', 'the-wild-wadi-waterpark', 'The Wild Wadi Waterpark is an outdoor waterpark located on the coast of Dubai.', '<p>The Wild Wadi Waterpark is an outdoor waterpark located on the coast of Dubai. The park has a number of pools, rides, slides, and activities and is considered the largest waterpark in the world.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa42f4ed4909da1a0eb9_the-wild-wadi-waterpark.jpeg', 2, 1, 6, 25, 55, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 2, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('The Wild Wadi Waterpark', 'the-wild-wadi-waterpark-f', '', '<p id="">The Wild Wadi Waterpark is an outdoor waterpark located on the coast of Dubai.&nbsp; The park has a number of pools, rides, slides, and activities and is considered the largest waterpark in the world.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63cb17aafc84bb7994d1969b_wild-wadi-waterpark_0.jpeg', 2, 1, 6, 0, 0, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('The WWII Oil Storage Tunnels', 'the-wwii-oil-storage-tunnels', '', '<p id="">The WWII Oil Storage Tunnels are a unique and educational attraction. They were built during World War II to protect the oil stored in Darwin from Japanese bombing raids. However, the tunnels took so long to complete and by the time they have finished, the risk from bombing was no longer there. They have never stored oil, but tunnels five and six are open to visitors and are an impressive reminder of man’s ingenuity.&nbsp; </p>', '', '', 8, 1, 8, 0, 0, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Thean Hou Temple', 'thean-hou-temple', '', '<p>This syncretic temple with elements of Buddhism, Confucianism and Taoism is a grandiose structure. It represents a successful combination of modern architectural techniques and authentic traditional design featuring imposing pillars, spectacular roofs, ornate carvings, and intricate embellishments. Its grand architecture has made it one of the best tourist destinations! The front entrance of the temple features a multi-arched gateway with red pillars, the color is symbolic of prosperity and good fortune.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8faafdd02fa0a4cacb7b3_thean-hou-temple.jpeg', 1, 1, 1, 3, 102, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Times Square', 'times-square', '', '<p id="">Times Square is a neighborhood and major commercial intersection in midtown Manhattan. &nbsp;It is also a top tourist destination and entertainment center, at the junction of Broadway and 7th Avenue. It is the primary location for New Years Eve celebrations in the United States.</p>', 'https://s3.us-west-2.amazonaws.com/assets.exploro.com/demo/Times+Square+New+York+4K.mp4', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63cb17abb9a07159c2665214_new-york-times-square.jpeg', 6, 1, 7, 0, 0, '', '', 'Manhattan, NY 10036', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Times Square', 'times-square-c', '', '<p>Celebrated by tourists, avoided by locals; you can’t visit New York without experiencing Times Square. The looming digital billboards and flashing colors of the screens from Broadway to Seventh Avenue have been featured in every form of popular media, from movies to video games. At its most crowded is during NYC’S New Year’s Eve celebration, where the midnight ball-drop you stream on TV every year takes place in person.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8f9f48f0aa20d9c7e0a84_times-square.jpeg', 6, 1, 7, 41, 74, 'info@timesquare.com', '555-555-5555', '332 W. 44th St. Edison ParkFast - 332 W. 44th St. Garage', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 4, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Top of the Rock', 'top-of-the-rock', 'See Central Park and the Upper East and West Sides, the 57th Street skyscrapers, and Harlem, all the way to Yankee Stadium in the Bronx and beyond.', '<p>Believe the hype. Top of the Rock’s three levels of indoor and outdoor observation decks deliver spectacular, unobstructed views of the city skyline. With terraces facing east, west, north, and south, Top of the Rock’s panoramic sights make it the ultimate observation deck in NYC. At the pinnacle of 30 Rockefeller Plaza’s 70 floors, it’s one of the top things to do in New York City.We are updating the Top of the Rock experience! Beginning in February 2023, the mezzanine level and the observation decks will be under construction, but we will remain open. Please note that access to specific areas may be limited while we work hard to bring exciting new features to Top of the Rock!</p>', 'https://s3.us-west-2.amazonaws.com/assets.exploro.com/demo/Top+of+the+Rock+New+York+October+2021+%5B4K%5D+(Part+2).mp4', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63cc82a4a3308a4d773cbffb_25815fd66041995e498ce3a7a606bcdd6e3713fd-2220x976.jpeg', 6, 1, 7, 0, 0, 'info@toprock.com', '555-555-5555', '45 Rockefeller Plaza, 10111, New York', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 4, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Torget', 'torget', '', '<p>Torget is an island just offshore Bronnoysund on the mainland. The island has about 800 inhabitants and is known for its good fishing areas. It is also home to the Torghatten granite mound.</p>', '', '', 1, 1, 1, 65, 12, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Torghatten', 'torghatten', '', '<p>Torghatten is a famous granite mound near Bronnoysund with a natural throughway. Located on the island of Torget, visitors can walk through the naturally formed tunnel on defined pathways.</p>', '', '', 1, 1, 1, 65, 12, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Tower Bridge', 'tower-bridge', '', '<p id="">Tower Bridge is a combined bascule and suspension bridge built in London in the late 19th century. The bridge crosses the Thames River close to the Tower of London and has become a world-famous symbol of the city. It is sometimes confused with London Bridge which is located a half mile upstream.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63cb17abd802dfe9af66d7c3_tower-bridge.jpeg', 11, 1, 11, 0, 0, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 3, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Trafalgar Square', 'trafalgar-square', '', '<p id="">Trafalgar Square is a public square that features several popular attractions including the National Gallery and the National Portrait Gallery. Besides the slew of museums, shops, and cafes, you should also check out the famous statues, columns, and fountains that are scattered throughout the square. </p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63cb17acea97be093eb497b3_pexels-szymon-shields-9596429.jpeg', 11, 1, 11, 0, 0, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 3, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Treriksroysa', 'treriksroysa', '', '<p>This is where Sweden, Norway, and Finland meet. The monument itself that marks this tripoint is a yellow dome-shaped stone located on Lake Goldajarvi. One can reach this destination by walking or by boat.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa483c56651ba66b6743_g.jpeg', 1, 1, 1, 70, 30, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Trick Eye Museum', 'trick-eye-museum', '', '<p>The world-renowned 3D optical illusion art museum, is the hottest attraction in Phuket.Thisis a great place to soak up some local culture and appreciate the unique things the area has to offer.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa865ee8110e34aacd77_trickemuseum.jpeg', 1, 1, 1, 8, 98, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Triechville Market', 'triechville-market', '', '<p>The Treichville Market is the largest open-air market and bazaar in Cote d’Ivoire and is in the central part of the city. It features restaurants, shops, and concessions and displays the region’s local crafts, pottery, produce, fabrics, and food.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa7d616f6d0d6858283c_treichville-market.jpeg', 1, 1, 1, 5, 4, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Trollholmsund', 'trollholmsund', '', '<p>Trollholmsund is a nature and wildlife preserve near Lakselv. The preserve has many places to hike, walk, and relax.</p>', '', '', 1, 1, 1, 70, 25, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Trollstigen', 'trollstigen', '', '<p>Trollstigen is a winding road, known as one of the most dangerous in the world that makes its way through the mountains near Namsos. The views are stunning and show off the gorgeous green mountains in the summer and the snowy winter peaks in the winter. Check out the viewpoint or try out the drive for an adventure you won’t soon forget.</p>', '', '', 1, 1, 1, 65, 11, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Tromso Cathedral', 'tromso-cathedral', '', '<p>Tromso Cathedral is the furthest north Lutheran cathedral located at the center of the city and is a truly beautiful building. It was built in 1861 and is the only wooden cathedral in all of Norway. The inside is beautifully decorated with golden chandeliers and a 19th-century resurrection painting by Adolph Tidemand that hangs above the church altar.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8f9e2b3a4cb23c428690c_nor-2016-tromso-tromso_cathedral.jpeg', 1, 1, 1, 70, 19, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Trondelag Folk Museum', 'trondelag-folk-museum', '', '<p>The folk museum has a replica of a 16th-century Viking Village and a museum with displays and exhibits that present the history and culture of Trondheim.</p>', '', '', 1, 1, 1, 63, 10, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Trondenes Church', 'trondenes-church', '', '<p>Just outside of Harstad, lies one of the northernmost medieval stone churches. Despite its age, it’s still very well preserved. The exterior was built to ward off bandits, as the walls were designed to look heavier than they are. It currently hosts Catholic mass and seats about 300 people.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa3a3c56658f5e6b660b_36827583462_caa5b8516e_c.jpeg', 1, 1, 1, 69, 17, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Trondheim Kunstmuseum', 'trondheim-kunstmuseum', '', '<p>The Kunstmuseum is an art museum dedicated to art from Norwegian artists and artists world wide.</p>', '', '', 1, 1, 1, 63, 10, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Tunku Abdul Rahman National Park', 'tunku-abdul-rahman-national-park', '', '<p>Tunku Abdul Rahman National Park is a group of five islands located 3 to 8 km off the shore. Manukan Island is the second largest island in the National Park, and Malaysia’s first marine national park. It’s the most popular island with local residents. There are beaches, snorkeling, hiking, and a large resort.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa495ee8110ef6aacb1b_manukan-island.jpeg', 1, 1, 1, 6, 116, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Turtle Cove', 'turtle-cove', '', '<p>Turtle Cove is a fantastic beach with a phenomenal coral reef. It is one of the busier beaches in Providenciales, as it has many shops and vendors surrounding the area. In addition, there is a marina that hosts many water activities that are fun for the whole family.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa2809d76d718232093e_provedenciales-turtle-cove.jpeg', 1, 1, 1, 22, 72, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Turtle Islands Park', 'turtle-islands-park', '', '<p>Turtle Islands Park is the perfect nature getaway. It is located within the Turtle Islands, a collection of islands lying in the Sulu Sea, around 3kilometers north of Sandakan. The park is known for the green and hawksbill turtles that lay their eggs on the beaches of the islands. The park covers an area of 17.4 km².</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa1ecf6b6c07d2f0143a_turtle-island_0.jpeg', 1, 1, 1, 6, 118, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Tuti Island', 'tuti-island', '', '<p>Tuti Island is in Khartoum where the Blue Nile and the White Nile join to form the main Nile River. The island has remained undisturbed exhibiting many plants and grasslands native to the area.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa46b3a4cb8628286da7_tuti-island.jpeg', 1, 1, 1, 16, 33, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Ukumari Park', 'ukumari-park', '', '<p>Ukumari Park is home to the unique Ukumari Zoo.Wildlife from around the world lives in natural surroundings in this large zoo. A tour through the zoo takes approximately three hours.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8f9e2751715255cc3d1cc_ukumari-park.jpeg', 1, 1, 1, 5, 76, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Union Square', 'union-square', 'Union Square is located in downtown San Francisco and is known as the heart of the city. It is 2.6 acres and lined with dozens of restaurants and shops.', '<p>Union Square is located in downtown San Francisco and is known as the heart of the city. It is 2.6 acres and lined with dozens of restaurants and shops. This square is where you can truly dive into the local culture of this city, especially in the theater district. Union Square also includes the central shopping, restaurants, and hotels surrounding the plaza for several blocks. You will have a lovely time walking around this square.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fab6cd42535d38885426_union-square.jpeg', 1, 1, 1, 38, 122, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Union Station Whispering Arch', 'union-station-whispering-arch', '', '<p>This beautiful arch sits in Union Station and is known for its outstanding acoustics. Union Stations shows off a beautiful stained glass window, but that’s not the outstanding feature everyone is talking about. Stand anywhere in the station with a friend or neighbor nearby and whisper-- can you hear each other? Thanks to the station’s acoustics, even a whisper can be heard from wherever you are standing. It’s definitely work giving a try if you are in the area.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa8e84a16004ef1946d5_union-station-whispering-arch.jpeg', 6, 1, 7, 39, 90, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 4, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Universal City Walk Hollywood', 'universal-city-walk-hollywood', '', '<p>The Universal City Walk Hollywood is one of the most iconic shopping streets in Burbank, and you can visit free without an actual ticket to the theme park. Along with trendy stores and yummy food booths, street performers will often frequent the walk. Something fun is always happening in this district, and it is great place to meet new people.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8faaef877272337d6f18e_burbank-universal-city-walk-hollywood.jpeg', 1, 1, 1, 34, 118, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Universal Studios Hollywood', 'universal-studios-hollywood', '', '<p>If you haven’t heard of Universal Studios Hollywood, where have you been? One of world’s most renowned theme parks is based on Hollywood. Rides and roller-coasters are each designed after Universal Studios-produced movie, essentially putting you into the film. For some autumn spooks, come during the fall when Universal hosts Halloween Horror Nights.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa70b67dcacdc33913e1_burbank-universal-studios-hollywood.jpeg', 1, 1, 1, 34, 118, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('University of Arizona Museum of Art', 'university-of-arizona-museum-of-art', '', '<p>The museum features displays, exhibits, and works of art from Tucson and the surrounding area to highlight the art and crafts of southwest America.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8f9e864b86c5fc53bca96_tucson-university-arizona-museum-art.jpeg', 1, 1, 1, 32, 111, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Usaquen Sunday Market', 'usaquen-sunday-market', '', '<p>On Sundays in Bogotá, there are lots of different things to do that are free only on this day of the week. The Usaquen Market is one of these. This market hosts tens of hundreds of vendors that sell food, treats, trinkets, clothes, and arts and crafts through the afternoon each Sunday in the city. It’s a great spot to browse for souvenirs!</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa37fccaf9100fcd4126_usuquen-street-market.jpeg', 1, 1, 1, 5, 74, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('USS Massachusetts', 'uss-massachusetts', '', '<p>Originally a battleship, the USS Massachusetts was sunk in 1921 during a training exercise in Pensacola Bay. Today, tourists can visit the wreckage that has been turned into a man-made reef. Visitors can even rent equipment and dive in the Bay to see the USS Massachusetts and the sea life growing around and in it.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa4c8f0aa246b57e1276_uss-massachusetts.jpeg', 1, 1, 1, 30, 87, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Utsira Island', 'utsira-island', '', '<p>To experience small-town life in Norway, venture to Utsira Island, where there are only 200 residents in this small village. A ferry is required to get to Utsira Island, and once there, you can explore the natural beauty while talking to locals, eating in the local café, and relaxing in the village.</p>', '', '', 1, 1, 1, 59, 5, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Valle de Angeles', 'valle-de-angeles', '', '<p>The Valle de Angeles is located just outside Tegucigalpa and is a colonial town surrounded by mountains. Visitors will enjoy the views, local culture, cuisine, and shopping at the many artisan booths.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8faaab67dca618f39152f_tegucigalpa-valle-de-angeles.jpeg', 1, 1, 1, 14, 87, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Vancouver Aquarium', 'vancouver-aquarium', '', '<p>See all types of marine life in the state-of-the-art Vancouver Aquarium. A visit to the aquarium includes several shows featuring marine life.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8faa4b67dca1d053914f8_vancouver-aquarium.jpeg', 1, 1, 1, 49, 123, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Vår Frelsers Church', 'var-frelsers-church', '', '<p>Vår Frelsers Church (Our Savior’s Church) is located in the center of Haugesund. As the city grew its residents insisted on a church of their own. In 1890 the government gave approval to start construction on a church. Construction began in 1899 and the church was completed in 1902. Guests are welcome to sit and enjoy this historical building.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8f9bcf877276e63d6e679_d_0.jpeg', 1, 1, 1, 59, 5, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Varanger Museum', 'varanger-museum', '', '<p>The Varanger Museum is located in Vardo, along the Varanger Fjord. The history of Norway’s northern lands is documented and recorded here.</p>', '', '', 1, 1, 1, 70, 30, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Varden Watch Tower', 'varden-watch-tower', '', '<p>The Varden Watch Tower sits at the center of Kristiansund and provides amazing views. The tower is 78 meters tall and plays a significant role in the history and development of Kristiansund. It was previously used by ship pilots during hostile times but is open for anyone to take in the view of either the shipping fairway, the city of Kristiansund, the fjords, or its mountainous region.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa93e7a1f6f1c3891945_s_1.jpeg', 1, 1, 1, 63, 8, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Victoria Fall Zip Line', 'victoria-fall-zip-line', '', '<p>The Victoria Fall Zip Line passes over the river. The falls are beautiful from this perspective.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8f9fa09d76d59fc32076f_victoria-fall-zip-line.jpeg', 1, 1, 1, 18, 26, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Victoria Falls', 'victoria-falls', '', '<p>Victoria Falls is a beautiful waterfall on the Zambezi River and is considered to be the largest waterfall in the world. It forms the border for the countries of Zambia and Zimbabwe, and the area around the falls provides a rich habitat for plants and animals.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa49b67dca3e6c391323_victoria-falls.jpeg', 1, 1, 1, 18, 26, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Victoria Falls Bridge', 'victoria-falls-bridge', '', '<p>Victoria Falls Bridge, built in 1905, crosses the Zambezi River just below Victoria Falls and is built over the second gorge of the falls.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8f9ff064c5a5131a62894_victoria-falls-bridge.jpeg', 1, 1, 1, 18, 26, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Victoria Falls National Park', 'victoria-falls-national-park', '', '<p>Victoria Falls National Park in north-western Zimbabwe protects the south and east bank of the Zambezi River around the world-famous Victoria Falls. The park extends along the Zambezi River from six miles above the falls to twelve miles below the falls.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa34dd02fa6d33acaf6b_victoria-falls-national-park.jpeg', 1, 1, 1, 18, 26, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Victoria Falls Rafting', 'victoria-falls-rafting', '', '<p>Rafting down the Zambezi River is a favorite for tourists and guests. Trips of varying times and ages are available.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa4d84a1600234194320_victoria-falls-rafting.jpeg', 1, 1, 1, 18, 26, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Victoria Island', 'victoria-island', '', '<p>The island is the location of the original founding of Lagos. Today, it is an affluent area and a center for tourism, entertainment, and finance. There are numerous shopping districts and restaurants featuring European cuisine and local Nigerian favorites.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8f9d3b3a4cb2e77286845_lagos-victoria.jpeg', 1, 1, 1, 6, 3, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Victoria Market', 'victoria-market', '', '<p>The Victoria Market is a large market on Mahe where residents can sell produce, crafts, carvings, jewelry, pottery, and fabrics. Restaurants in the market feature dishes native to Seychelles.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa9f975f812006d2f360_victoria-market.jpeg', 1, 1, 1, 5, 55, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Victoria Peak', 'victoria-peak', '', '<p>Victoria Peak is a hill on the western half of Hong Kong Island that offers great views of the city. It is also known as Mount Austin, and locally as The Peak. With an elevation of 552 meters, it is the highest hill in Hong Kong island, and a popular tourist destination and lookout point.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8f9bbec00414fcaaa8da2_victoria-peak.jpeg', 1, 1, 1, 22, 114, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('View the Midnight Sun/Northern Lights', 'view-the-midnight-sun-northern-lights', '', '<p>Leknes is so far north that the summer sun does not set. It is considered the land of the midnight sun.Leknes also has the reputation of being an excellent place to view the Northern Lights.</p>', '', '', 1, 1, 1, 68, 14, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('View the Northern Lights', 'view-the-northern-lights', '', '<p>The Northern Lights are one of the seven natural wonders of the world. This natural phenomenon is known by several names, such as the Aurora Borealis, or simply just Aurora. These lights are a must-see for anyone who comes to visit Northern Norway. While you can see the lights year-round, November to February is usually the best time to visit, since this season is when Norwegian nights are the longest.</p>', '', '', 1, 1, 1, 52, 5, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Viewpoint Hammerfest', 'viewpoint-hammerfest', '', '<p>The Viewpoint Hammerfest is a pier and dock in Hammerfest Sound where ferries and boats find the shore. The pier is a boardwalk of sorts, with places to watch the boats on the harbor, visit the shops and eat at cozy restaurants.</p>', '', '', 1, 1, 1, 71, 24, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Viking Ship Museum', 'viking-ship-museum', '', '<p>The Viking Ship Museum focuses on the history and culture of the Viking people. The museum has artifacts from the Viking period including several Viking ships that have been unearthed and restored.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8f9be3c5665d3ce6b52cc_oslo-viking-ship-museum.jpeg', 1, 1, 1, 60, 11, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Viking Village Sogndal', 'viking-village-sogndal', '', '<p>Norwegian Viking villages are a large part of the landscape of Norway. Visit villages with buildings built in the Viking way and walk through to learn about the way Vikings lived historically. Guides give tours dressed in Viking outfitting and you can wander on your own and and enjoy Viking living</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8f9cddd02fa42c2aca216_viking-village.jpeg', 1, 1, 1, 61, 7, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Villa of the African Queen', 'villa-of-the-african-queen', '', '<p>The Villa is the hotel where the cast of the famous movie ‘The African Queen” stayed during the filming of the movie.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa0fcbe2f645a6eceb70_villa-of-the-african-queen.jpeg', 1, 1, 1, 12, 28, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Virginia B. Fairbanks Art and Nature Park', 'virginia-b-fairbanks-art-and-nature-park', '', '<p>This park is open from sunrise to sunset and shows off a unique display of contemporary art sculptures. Comprising about 100 acres, this nature park offers woodlands, wetlands, meadows, and art installations that make each visit unique. There is also an art museum adjacent to the park.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa2064b86cf83e3bcd9b_indianapolis-museum-of-art-100-acres-003-medium.jpeg', 1, 1, 1, 40, 86, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Visit Boyabreen Glacier', 'visit-boyabreen-glacier', '', '<p>The walk to Boyabreen Glacier is only around five minutes from the parking lot to the turquoise lake at the bottom of the glacier. During the winter months, you can walk right up to the glacier’s base so that you can look directly up. This is not advised during the summer unless you want to be hit by falling chunks of ice. Come enjoy the glacier while you still can, as scientists believe it may melt in the next decade or so due to a warming climate.</p>', '', '', 1, 1, 1, 61, 7, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Visit Delirio, Colombia’s Famous Salsa Circus', 'visit-delirio-colombias-famous-salsa-circus', '', '<p>Delirio is the world-famous performance of salsa dancing professionals. People come from all over the world to watch the dancers at Delirio.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa6175171520a7c3dee6_delirio.jpeg', 1, 1, 1, 3, 77, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Visit Ft. Desoto Park', 'visit-ft-desoto-park', '', '<p>Ft. Desoto Park is a collection of islands off the coast of St. Petersburg that is linked together. The islands remain in a natural state, and visitors come from all over the area to enjoy the beaches.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa0d7517151b76c3d4d0_st-petersburg-desoto-park.jpeg', 1, 1, 1, 28, 83, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Visit Hamningberg', 'visit-hamningberg', '', '<p>Hamningberg is an abandoned fishing village but was once one of the largest fishing villages, housing over 250 people. It survived German troops in WWII who had briefly taken over and fled when Russian forces were rumored to be approaching. This unique isolated town still preserves both the homes and coastal culture.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa93cf6b6c41ddf01ab4_1024px-boathouses_in_hamningberg.jpeg', 1, 1, 1, 71, 30, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Visit Mayan Ruins', 'visit-mayan-ruins', '', '<p>On the northern part of the island of Cozumel, hidden in the jungle, is the San Gervasio Archeological Site. This site is home to Mayan ruins. Visitors to the site can tour the few remaining structures and learn about the Mayan culture at the small museum.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa89b67dca719c391470_cozumel-mayan-ruins.jpeg', 1, 1, 1, 21, 87, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Visit Nordkapp', 'visit-nordkapp', '', '<p>Many visitors to Honningsvag are initially there to visit Nordkapp. The Nordkapp is a 1007-foot-high cliff that rises above the Atlantic Ocean and is considered the northernmost point in all of Europe. Visitors to this location can take in the gorgeous views, learn about the area or visit the gift shop.</p>', '', '', 1, 1, 1, 71, 26, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Visit Old San Juan', 'visit-old-san-juan', '', '<p>Cobblestoned Old San Juan is a beautiful spot to stop. Rich with culture and history, Old San Juan shows off beautifully colorful and charming street views,food experiences, and local music within walking distance of the ocean. There are activities for all ages and flavors in Old San Juan. Move to the beat of the salsa music ringing through the streets or tour old buildings and take photos of the ancient architecture.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8f9c25ee8115821aabaea_san-juan-forts.jpeg', 1, 1, 1, 18, 66, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Visit the Antique Shops', 'visit-the-antique-shops', '', '<p>Despite being so far north, Vadso is home to several charming antique shops. You can find art, jewelry, collectibles, and vintage houseware, to name a few.</p>', '', '', 1, 1, 1, 70, 30, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Visit the Cities Indoor Markets', 'visit-the-cities-indoor-markets', '', '<p>Palma de Mallorca has several interesting indoor markets. Mercat de l’Olivar and Mercat de Santa Catalina have both been around since the 19th century, and are still popular today. Both of them are open every day (except Sunday), and sell everything from vegetables and baked goods to spices and cheeses. Besides just wandering through and watching the locals shop, this is also a great place to grab a bite to eat!</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa97cd425346a6885287_mercat_de_lolivar.jpeg', 1, 1, 1, 40, 3, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Visit the Lost City', 'visit-the-lost-city', '', '<p>Ciudad Perdida, The Lost City, was built by the Tayrona people over 1,000 years ago, and became an attraction after it was uncovered in the 1970’s. The only way to visit The Lost City is via a multi-day hike.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8f9e9b3a4cb7080286993_lost-city_0.jpeg', 1, 1, 1, 11, 74, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Visit the Parklands of Floyds Fork', 'visit-the-parklands-of-floyds-fork', '', '<p>Floyds Fork consists of five different parks with playgrounds, parks, ponds, splash pads, fishing, and picnic areas year-round. Enjoy some time out of the city in this beautiful spot of green in urban Kentucky.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8f9e5fccaf98531cd3621_floyds-fork-parklands-louisville-01.jpeg', 1, 1, 1, 38, 85, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Visit The Seven Sisters', 'visit-the-seven-sisters', '', '<p>The Seven Sisters mountain range is over six miles long! It’s also one of Norway’s most well-known and popular hiking destinations. The mountain range has seven peaks:Botnkrona at 1,072 metersGrytfoten at 1,019 metersSkjæringen at 1,037 metresTvillingene (The Twins) at 945 and 980 metersKvasstinden at 1,010 metresBreitinden at 910 metersYou can choose your route based on your hiking preferences and ability.</p>', '', '', 1, 1, 1, 62, 7, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Visit the Talbot Island State Park', 'visit-the-talbot-island-state-park', '', '<p>The Little Talbot Island State Park and the Big Talbot Island State Park are just north of Jacksonville.Both Islands have great beaches, and both have retained their natural beauty.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8f9c8975f812782d2e491_jacksonville-state-parks.jpeg', 1, 1, 1, 30, 81, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Walk Around the Oldtown of Kristiansund', 'walk-around-the-oldtown-of-kristiansund', '', '<p>Kristiansund is surrounded by beauty and history. The city was originally destroyed during WWII. However, the reconstruction of the city provided unique new architecture, embellished in shades of red, yellow, and green, giving Kristiansund the nickname “the Polychrome City” (the Colorful City).</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8faa28f0aa2fa9e7e1879_45864480451_59db192994_c.jpeg', 1, 1, 1, 63, 8, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Walk the Big Four Bridge', 'walk-the-big-four-bridge', '', '<p>The Four Bridge Park is a pedestrian bridge that links Louisville to Jefferson, Indiana, which offers the best view of the Ohio river. Cycle or walk the bridge for a memorable experience, highlighted at night by gorgeous LED lights that span the length of the bridge.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa25c4030a289914774f_four-bridge-louisville.jpeg', 1, 1, 1, 38, 86, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Walk The Golden Gate Bridge', 'walk-the-golden-gate-bridge', '', '<p>The Golden Gate bridge is a gorgeous spot to enjoy a walk. Walk through Golden Gate Park on your way to the bridge for an extended visit. The bridge contains walking lanes in both directions and offers plenty of gorgeous Instagram-worthy photo opportunities.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8f9f4cbe2f63638ece5b2_gol.jpeg', 1, 1, 1, 38, 122, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Walk the Rio Cali', 'walk-the-rio-cali', '', '<p>The Rio Cali is a peaceful paved Riverwalk along the Cali River in central Cali. On one side is the river, and on the other is a selection of outdoor cafes, nightclubs, shops, and restaurants.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8f9c9e7a1f6d81b88f964_rio-cali.jpeg', 1, 1, 1, 3, 77, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Walk through Historic Downtown', 'walk-through-historic-downtown', '', '<p>Manchester’s old city vibe is hard to beat. A stroll downtown offers a chance to create an adventure of your own. Check out the many historic old mill buildings, restaurants, bars, cafes, and shops that line this downtown area. Come after the sun sets and this is the spot to be for magical Manchester nightlife, right in the middle of downtown.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa307517157e8dc3da0d_old-town-manchester.png', 1, 1, 1, 19, 99, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Walk with Lions Victoria Falls', 'walk-with-lions-victoria-falls', '', '<p>Walk with Lions Victoria Falls is a wildlife conservation area near the falls, its primary emphasis is on the care and preservation of the African Lion which is indigenous to the area.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8f9f9f4ed491c3f1a05fc_walk-with-lions-victoria-falls.jpeg', 1, 1, 1, 18, 26, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Walt Disney Studios', 'walt-disney-studios', '', '<p>When in southern California, you should definitely plan a visit to Anaheim, home of Walt Disney Studios’ California location and the first Disney park. Spanning fifty-one acres of land, here are the headquarters for all things Disney. Cross between Disneyland Park and California Adventure and try your best to avoid the tourist crowds!</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8f9cc975f810d1bd2e4db_burbank-walt-disney-studios.jpeg', 1, 1, 1, 34, 118, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('War Remnants Museum', 'war-remnants-museum', '', '<p>War Remnants Museum was formerly the museum of Chinese and American War Crimes. This popular location is full of US sources of information that dictate what happened, but even then, rarely do westerners hear the victims of US military action tell their own stories. There are also real armored vehicles, artillery pieces and infantry weapons displayed outside.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa5f4ffa1679bc993458_war-remnants-museum.jpeg', 1, 1, 1, 11, 107, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
