INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Weequahic Lake Trail', 'weequahic-lake-trail', '', '<p>This 2-mile loop trail is an easy hike that takes about 45 minutes to complete. It is paved and wheelchair accessible, with beautiful views of the lake and surrounding forest. Spot native flora and fauna on the trail, and feel free to bring kids as the hike is low intensity.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa9cfccaf90256cd4b05_weequahic-lake_0.jpeg', 1, 1, 1, 41, 74, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('West Bay Beach', 'west-bay-beach', '', '<p>Year after year, West Bay Beach is one of the top five beaches in the Caribbean. The natural beach is clean and white and has a place for snorkeling, water sports, and places to eat and drink.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa0b8f0aa229317e0b1b_west-bay-beach.jpeg', 1, 1, 1, 16, 87, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Westminster Abbey', 'westminster-abbey-5', '', '<p id="">Westminster Abbey, formally titled The Collegiate Church of Saint Peter at Westminster, is a large, gothic church in the Westminster area of London. It is the location for the coronation of British monarchs and the site of Royal and state funerals.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8f9c2cbe2f64bfeece1ca_wesminster-abbey.jpeg', 11, 1, 11, 0, 0, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 3, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Westmoreland Museum of Art', 'westmoreland-museum-of-art', '', '<p>The art museum is a must-see! Located just outside of Latrobe, the Westmoreland Museum of American Art showcases paintings, sculptures, and photography from the colonial era to modern times.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8f9e4616f6d78fe582027_latrobe-westmoreland-museum-art.jpeg', 1, 1, 1, 40, 80, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Wet’n Wild Emerald Pointe Water Park', 'wetn-wild-emerald-pointe-water-park', '', '<p>Wet’n Wild Emerald Pointe Water Park is an exciting place to visit in Greensboro. There are many water slides and activities to enjoy, encouraging visitors to relax and have a thrilling time. The park has water slides for all age groups, making it the perfect place to spend all day.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8f9d3c4030a7c60147165_greensboro-water-park.jpeg', 1, 1, 1, 36, 80, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Whale and Dolphin Sightings', 'whale-and-dolphin-sightings', '', '<p>Whales and dolphins are attracted to the cold clear waters of the Fjord, which is why there were once several whale fisheries in the area. Tour guides can take you to spots to see the beautiful whales and dolphins that live in the waters.</p>', '', '', 1, 1, 1, 18, 77, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Whale & Dolphin Sightings', 'whale-dolphin-sightings', '', '<p>The fjords of Norway are a natural habitat for whales and dolphins, who are attracted to the cold clear waters. Visitors to Hasvik can see these beautiful mammals from the docks or take one of the boat tours to see them up close.</p>', '', '', 1, 1, 1, 18, 77, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Whale Watching', 'whale-watching', '', '<p>Andenes is the premier whale watching location in all of Norway and is known as the "Whale Kingdom of the North." Visitors who experience a whale-watching expedition can view Orcas, Humpbacks, Sperm, and Fin whales in their natural habitat while feeding on the local herring population.</p>', '', '', 1, 1, 1, 69, 16, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Whale Watching', 'whale-watching-b', '', '<p>If you want a memorable experience, book a seat on one of the many whale-watching tours on Puget Sound. Some fantastic wildlife is seen on these expeditions, including Humpback Whales, Gray Whales, and Orcas.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa5009d76d937f320d46_seattle-whale-watching.jpeg', 1, 1, 1, 48, 122, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Whale Watching in Alta Fjord', 'whale-watching-in-alta-fjord', '', '<p>November and January are the best times for whale watching. Thanks to Alta’s unique location you can spot various different whales from sperm whales to killer whales and humpbacks. For a small fee, you can ride a boat to see the whales up close!</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa1f185e34a4d7b8a5aa_pexels-pixabay-289324.jpeg', 1, 1, 1, 70, 23, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('White River State Park', 'white-river-state-park', '', '<p>For a day filled with entertainment, White River State Park is the place to go. This park creates an urban sanctuary in a busy city, offering recreational, cultural, and educational events and attractions. A number of other cool Indy attractions are located in the state park limits, including the Indianapolis Zoo, Amphitheater, NCAA Hall of Champions, and Victory Field, home of the Indianapolis Indians. There is also a number of free-standing art exhibits hosted here throughout the year.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa5a975f8161ffd2ee55_indianapolis-white-river.jpeg', 1, 1, 1, 40, 86, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Whitewater Park', 'whitewater-park', '', '<p>Whitewater Park is in downtown Reno and is a manufactured installation to simulate whitewater rafting. Visitors can rent kayaks and inner tubes or take rafts down the half-mile course full of rapids.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8f9e9185e34cb1db8a3b6_reno-whitewater-park.jpeg', 1, 1, 1, 40, 120, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Whitewater Rafting', 'whitewater-rafting', '', '<p>Add some excitement to a weekend in Richmond by rafting down the James River. The course runs for 7.5 miles and contains Class III and IV rapids for seasoned river-rafters. The Upper James River is a more relaxing float, available at a slower pace for families or beginners. For something extra special, check out the custom excursions offered, including firework displays and private dinners.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa45e7a1f61214890d66_richmond-rafting.jpeg', 1, 1, 1, 41, 76, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Wild is Life', 'wild-is-life', '', '<p>Wild is Life features wildlife native to the Harare area in their natural settings. The sanctuary also raises orphan animals when they are found. The park can be toured in small groups and is a must see for those interested in southern African wildlife.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa76198c402259527fcf_wild-is-life_0.jpeg', 1, 1, 1, 18, 31, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Wilderness Beach', 'wilderness-beach', '', '<p>Wilderness Beach is known as one of the best surfing beaches in Aguadilla (and there are several)! This beach is easily accessible, but is for the slightly more experienced. New surfers should hire a surfing pro to assist them if they are surfing foreign waters for the first time. If you are staying all day, check out the coves nearby and search the rocks for tiny crabs!</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8f9b7fccaf90085cd329a_aguadilla-wilderness-beach.jpeg', 1, 1, 1, 18, 67, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Wildlife Rescue Center', 'wildlife-rescue-center', '', '<p>San Jose has several wildlife rescue centers within and just outside of the city. Exotic animals and birds are taken to these rescue centers when injured or unable to live in the wild. Sloths, toucans, monkeys, pumas, and alligators are some of the many animals one may see.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8f9c5b3a4cb39062867a4_wildlife-rescue-museum.jpeg', 1, 1, 1, 39, 91, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('William Howard Taft National Historic Site', 'william-howard-taft-national-historic-site', '', '<p>President Taft grew up in Cincinnati and his home has become a museum on his life and his presidency.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8f9f809d76d31ae32074a_cincinnati-william.jpeg', 1, 1, 1, 39, 85, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Windermere', 'windermere', '', '<p>A suburb of Orlando, Windermere is a small town in a big city atmosphere where many come to retire. Main Street is lively and electric, with food trucks, restaurants, events, and festivals throughout the year. This is the perfect location to check out the town’s chain of lakes, connected with canals to make a peaceful afternoon by the water.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa9c975f816d15d2f305_orlando-windermere.jpeg', 1, 1, 1, 29, 82, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Winnie Palmer Nature Reserve', 'winnie-palmer-nature-reserve', '', '<p>At the Winnie Palmer Nature Reserve, named after the wife of golfer Arnold Palmer, there are two miles of walking trails, gardens, a pond, and a nature center for visitors to enjoy. The Nature Reserve promotes children’s well-being and health through nature exploration with its Nature Explore Classroom and Nature Playspaces in an effort to encourage positive bonds between children and nature.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa63cbe2f6bc77ecfb83_latrobe-winnie-palmer-nature-reserve.jpeg', 1, 1, 1, 40, 79, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Wisconsin State Capital', 'wisconsin-state-capital', '', '<p>With the government playing such a significant role as an employer in the city, you won’t want to miss this tour. Learn about the history of Madison and the city government’s plans for the future.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa5fa98ced87a489eb41_madison-state-capital.jpeg', 1, 1, 1, 43, 89, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Woodland Park', 'woodland-park', '', '<p>This park is picture perfect, with a picnic shelter for an afternoon with your family and a playground for the kids. The park also includes two majestic duck ponds where you can find a few people fishing any time of day. The Woodland Water Park is just adjacent to this park, making it a great location to spend time with friends and family or for large parties.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fab04ffa1650bf993cf7_kalispell-woodland-park.jpeg', 1, 1, 1, 48, 114, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Woodland Water Park', 'woodland-water-park', '', '<p>The Woodland Water Park is an exciting way to spend summertime days and offers many types of water-themed rides and attractions, perfect for family outings. The park offers swimming lessons for kids of all ages and gives those who know how to swim a place to practice their skills. If you’re the type of person who is more into exciting rides than swimming across a pool, there are various slides to try out and lazy pools to drift in.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa3cc4030a95a6147919_kalispell-water-park.jpeg', 1, 1, 1, 48, 114, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('World Amber Museum', 'world-amber-museum', '', '<p>The World Amber Museum is located in Charlotte Amalie, and if you have a free day, you should definitely schedule some time here. The gemstone of its namesake was primarily sourced and traded in the Virgin Islands. The museum today, boasts a vast collection of amber and showcases a waterfall built from over 12,000 individual pieces of the stone.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8f9d4616f6d5509581ee9_st-thomas-world-amber.jpeg', 1, 1, 1, 52, 0, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Worth Avenue', 'worth-avenue', '', '<p>Worth Avenue is a pedestrian-only street whose buildings are now shops, boutiques, restaurants, and bars. The area gets very active after dark and there is always something going on for the public.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa293c56652d3f6b5f89_worth-avenue.jpeg', 1, 1, 1, 27, 80, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('YAOWARAT ROAD', 'yaowarat-road', '', '<p>Yaowarat Road is the main road from Bangkok’s center to Chinatown. Chinatown is one of Bangkok’s most vibrant neighborhoods. Thanks to the mixture of Thai and Chinese culture, this part of town is unique and fascinating.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8f9c0dd02fa716baca169_yaowarat-road.jpeg', 1, 1, 1, 8, 98, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
