INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Ybor City', 'ybor-city', '', '<p>Ybor City was a neighborhood populated by immigrants from Cuba. Most of the population was employed to make Cuban cigars, and the area became the center for cigar manufacturing in the United States. It has been designated a National Historic Landmark District and has much to offer the Tampa area. Walk the streets of this historic neighborhood or take in some of the vintage shops in the area to experience the history that makes Ybor City an unrivaled stop on your Tampa vacation.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa0c09d76d21883207eb_ybor-city.jpeg', 1, 1, 1, 28, 82, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Yellow Green Farmers Market', 'yellow-green-farmers-market', '', '<p>Stop in at the Yellow Green Farmers Market for organic, fresh produce. Sample artisanal food at the stands here and learn more about sustainable farming in an open-air market that beats the grocery store any day.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa12dd02fa49e3aca91f_fort-lauderdale-yellow-green-farmers-market.jpeg', 1, 1, 1, 26, 80, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Yellowstone National Park and Grand Teton National Park', 'yellowstone-national-park-and-grand-teton-national-park', '', '<p>These parks are two of the most popular National Parks in the USA, and both of them have their primary entrance through Jackson Hole.You can find all the information needed to visit Yellowstone and Grand Teton National Parks in Jackson Hole.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa7ec4030a7538148d87_yellowstone-national-park-grand-teton-national-park.jpeg', 1, 1, 1, 43, 111, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Zambian Safaris', 'zambian-safaris', '', '<p>Zambian Safaris is near Ndola, just outside the copper belt, where the jungle is the richest. Safaris can be reserved based on the number of people, their age, and the animals for the hunt. People who want to see the animals and the area can book a safari. The hunting safari and sight-seeing safari are in separate locations for the safety of the participants.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8f9e6ec004143c5aa9224_zambian-safaris.jpeg', 1, 1, 1, 13, 29, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Zimbabwe Museum of Human Sciences', 'zimbabwe-museum-of-human-sciences', '', '<p>The Zimbabwe Museum of Human Sciences is the largest museum in Harare. The museum contains the seven-hundred-year-old Lemba artifact, Ngoma-lungundu, which some believe to be a replica of the Ark of the Covenant. It is the oldest wooden object ever found in southern Africa.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa81198c4088bc52807f_zimbabwe-museum-of-human-sciences_0.jpeg', 1, 1, 1, 18, 31, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Zippin Pippin Roller Coaster', 'zippin-pippin-roller-coaster', '', '<p>The Zippin Pippin Roller Coaster is a landmark in Green Bay. This wooden Roller Coaster was built in 1901 and remained one of the largest roller coasters ever made.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8f9db975f81e503d2e588_green-bay-roller-coaster.jpeg', 1, 1, 1, 45, 88, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Zócalo', 'zocalo', '', '<p>The Zócalo is the common name of the main square in central Mexico City. Prior to the colonial period, it was the main ceremonial center in the Aztec city of Tenochtitlan. The plaza used to be known simply as the "Main Square" or "Arms Square", and today its formal name is Plaza de la Constitución.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8fa60c4030a1b7f148c14_mexico-city-zocalo.jpeg', 1, 1, 1, 19, 99, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
INSERT INTO activity (name, slug, description_short, description_long, video_link, image_link, country, region, city, latitude, longitude, email, phone, address, website_link, tags, partner_vendor, image_link_2, thumbnail_link, continent, gallery, featured_activity) VALUES ('Zoodoo Zoo', 'zoodoo-zoo', '', '<p>Zoodoo Zoo is a great spot to bring the whole family for a full day of outdoor fun. There are many animals to see spread out over 90 hectares. The zoo caters to encounter packages, where you can see the animal feedings up close including animals as magnificent as lions.</p>', '', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8f9ccb3a4cb3f922867d4_1920x1080-Zoodoo%2520Zoo.jpeg', 1, 1, 1, 43, 147, '', '', '', '', '', 1, 'https://i.imgur.com/hfM1J8s.png', 'https://i.imgur.com/hfM1J8s.png', 1, ARRAY['https://i.imgur.com/hfM1J8s.png'], 999.9);
