CREATE TABLE city (
  id SERIAL PRIMARY KEY,
  name varchar(200) UNIQUE NOT NULL,
  slug varchar(250) UNIQUE NOT NULL,
  description_long TEXT NOT NULL,
  description_short TEXT NOT NULL,
  image_link TEXT NOT NULL,
  thumbnail_link TEXT NOT NULL,
  special_offer_image_link TEXT,
  video_link TEXT,
  gallery TEXT[] NOT NULL,
  -- getting around image link, text
  getting_around TEXT[] NOT NULL,
  -- food and drink image link, text
  food_and_drink TEXT[] NOT NULL,
  -- facts image link, text
  facts TEXT[] NOT NULL,
  -- hotel tab heading, tab text
  hotel TEXT[],
  -- restaurant tab heading text, tab text
  restaurant TEXT[],
  -- attraction tab heading text, tab text
  attraction TEXT[],
  -- shopping tab heading text, tab text
  shopping TEXT[],
  featured_city FLOAT NOT NULL,
  continent integer references continent(id) NOT NULL DEFAULT 1,
  country integer references country(id) NOT NULL DEFAULT 1,
  region integer references region(id) NOT NULL DEFAULT 1,
  latitude FLOAT DEFAULT 0.0 NOT NULL,
  longitude FLOAT DEFAULT 0.0 NOT NULL,
  tags TEXT,
  created TIMESTAMP WITH TIME ZONE DEFAULT CURRENT_TIMESTAMP NOT NULL,
  edited TIMESTAMP WITH TIME ZONE DEFAULT CURRENT_TIMESTAMP NOT NULL
);

CREATE TRIGGER trigger_row_edit_update_timestamp_for_city
BEFORE UPDATE ON city
FOR EACH ROW
EXECUTE FUNCTION update_updated_on();

