INSERT INTO city (name, slug, description_long, description_short, image_link, thumbnail_link, special_offer_image_link, video_link, gallery, getting_around, food_and_drink, facts, hotel, restaurant, attraction, shopping, featured_city, continent, country, region) VALUES ('Cali', 'cali', 'Cali has the fastest-growing tourist industry in Colombia. You can find many of the rumba and salsa clubs in the entertainment suburb of Juanito. In Cali’s oldest quarter, narrow streets with cobblestones feature renovated old houses-turned cafes, shops, and bars. As the only Colombian city with access to the Pacific Coast, Cali has become the economic dynamo of south Colombia and a magnet for tourists.&nbsp;', 'Santiago de Cali, more often known as simply Cali, is southwest Colombia’s most populated city, seated on the Pacific Coast, and, best of all: the salsa capital of the world.', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8d09bfb81052d49912021_cali-c1.jpeg', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8d09bfb81058e1b912022_cali-c2.jpeg', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63ca0aabed101e2274342f82_63c9ddb96dddae08d0ac69a8_63c9d6500c3d4b2fbeda7ac6_ExploroAd.jpeg', 'https://media-dev.exploro.com/inflight-media/cali-about-banner.jpg', ARRAY['https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8d09bfb810547df912023_cali-overview.jpeg','https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8d09bfb810506c2912020_cali-c3.jpeg','https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8d09bfb810579b0912027_cali-c4.jpeg'], ARRAY['https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8d09bfb81050e6e912026_cali-getting.jpeg','<p id="">There is no metro in Cali, but the Masivo Integrado de Occidente (MIO) runs through 95% of the city with a separate lane that allows the rider to view the city. Taxis and rental cars are also available at a low cost. Visitors can find MIO information at stations, hotels, and Visitor Information Kiosks.</p>'], ARRAY['https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8d09bfb810578cf912024_cali-fnb.jpeg','<p id="">Cali has molded the past into the present by creating dishes that have a unique fusion of the tastes of indigenous Colombian food with the flavors brought from Europe to the new world. Order the sancocho de gallina, a rich hen or fish stew cooked with avocados, rice, corn, yucca, and spicy peppers, if you’d like to try a local dish, with a glass of Colombian wine on the side of course!</p>'], ARRAY['https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8d09bfb81051bde912025_cali-facts.jpeg','<p id="">Cali has one of the most pleasant climates of a coastal city in South America due to its location in the Andean Mountains near the Pacific Coast. Cali has a population of 2.25 million residents. Cali is the third-largest city in Colombia after Bogota and Medellin. Spanish is the official language, but many residents speak English as a second. Cali has the fastest-growing economy of any city in Colombia. Much of that growth is due to international tourism.</p>'], ARRAY['Hotel Tab Heading','Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum'], ARRAY['Restaurant Tab Heading','Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidata