INSERT INTO city (name, slug, description_long, description_short, image_link, thumbnail_link, special_offer_image_link, video_link, gallery, getting_around, food_and_drink, facts, hotel, restaurant, attraction, shopping, featured_city, continent, country, region) VALUES ('Fort Myers', 'fort-myers', 'Fort Myers, located on Florida’s Gulf Coast located between Miami and Tampa, is famous for its abundance of shells. In fact, this Fort Myers contains more types of seashells than any other beach in North America! The Times Square shopping center is a fun spot here, with stores and restaurants that are frequented by both locals and tourists. Fort Myers was nicknamed the ’City of Palms’ after the imported Royal Palm trees that line the main street. Fort Myers is your paradise to play in the water and and fun in the sun. Try boating, fishing, hiking, biking, tennis, golf, and more.', 'Welcome to Fort Myers, Florida - a premier destination for travelers from all over the world. Situated on the Gulf of Mexico in southwest United States, Fort Myers is known for its warm weather, white sand beaches, and abundance of outdoor fun. Whether you’re looking to relax on the coast, try out some of the best fishing in the country, or explore the many attractions and shops in the area, this Florida gem has everything to offer!&nbsp;', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8d06ccaee1241a587e59e_fort-myers-c1.jpeg', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8d06ccaee12160387e5a1_fort-myers-c2.jpeg', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63ca0aabed101e2274342f82_63c9ddb96dddae08d0ac69a8_63c9d6500c3d4b2fbeda7ac6_ExploroAd.jpeg', 'https://media-dev.exploro.com/inflight-media/fort-myers-florida.mp4', ARRAY['https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8d06ccaee126ca587e59f_fort-myers-overview.jpeg','https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8d06ccaee124b3487e5a0_fort-myers-c3.jpeg','https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8d06dcaee122dc087e5a4_fort-myers-c4.jpeg'], ARRAY['',''], ARRAY['https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8d06ccaee121fb587e59d_fort-myers-fnb.jpeg','<p id="">Like many beach towns, Fort Myers specializes in sumptuous seafood. Some bites you’ll find on the menu here include scallops, oysters, Cuban sandwiches, and even Cajun-fried gator-tail bites-- because why not, it’s Florida! For dessert, start with a summery key lime pie. The restaurants here have a laid-back atmosphere with high-quality ingredients, fresh seafood and some delicious steak.</p>'], ARRAY['https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8d06ccaee12671d87e59c_fort-myers-facts.jpeg','<p id="">Fort Myers has the highest number of days with sunshine than any other Florida city.There are more shell varieties on Fort Myers’ beaches than anywhere else in the U.S.Thomas Edison was a resident of Fort Myers, and because of him, the whole city received electricity in 1898.In 1876, Fort Myers was home to just ten families.Fort Myers is the only U.S. city with a dedicated shell museum, The Bailey Matthews National Shell Museum.In the 1700s, the coast along Fort Myers was often frequented by pirates.</p>'], ARRAY['Hotel Tab Heading','Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum'], ARRAY['Restaurant Tab Heading','Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis 