INSERT INTO city (name, slug, description_long, description_short, image_link, thumbnail_link, special_offer_image_link, video_link, gallery, getting_around, food_and_drink, facts, hotel, restaurant, attraction, shopping, featured_city, continent, country, region) VALUES ('Sandnessjoen', 'sandnessjoen', 'Sandnessjoen is located on the island of Alsta and just became a town in 1999. This small port village is not far from the famous Seven Sisters Mountain range. The main industry of Sandnessjoen is fishing, so make sure to try the local seafood! Because of its location, Sandnessjoen is an important trading port and has quite a lot of boat traffic constantly coming in and out of its harbor.', 'Sandnessjoen, a town in southwestern Norway, is a transportation hub for the southwest region of the country, as well as a business and commercial center. In 1838 Sandnessjoen was established and today has a population of approximately 6,000, making it one of the largest municipalities in Norway. However, since Sandnessjoen is in the more populated southern part of Norway, it serves as a suburban enclave rather than an industrial region. Although there is some tourism, most people in Sandnessjoen live there while working outside the town in other municipalities. Despite this, many visitors come from around the world to see Sandnessjoen’s beautiful views and hike the mountains of the Seven Sisters mountain range.', '', '', '', 'https://media-dev.exploro.com/inflight-media/sandnessjoen-about-banner.jpg', ARRAY['','',''], ARRAY['','<p id="">Sandnessjoen is primarily a suburban and business center in Norway’s heavily populated southwestern region. Most people have their vehicles, but there is public transportation, in the form of a bus service, for those who visit or don’t want to drive. There are also taxis and rental cars available.</p>'], ARRAY['','<p id="">Many good restaurants in Sandnessjoen serve dishes from around the world as well as favorite Norwegian cuisines. If you would like to try a local dish, you should try the mussels, or any of Sandnessjoen’s fresh-caught seafood.</p>'], ARRAY['','<p id="">The currency in Sandnessjoen is the Norwegian Krone.Sandnessjoen has an arctic climate. The winters are long, cold, and wet, and the summers are brief, warm, and dry.The population of Sandnessjoen is 6,050.The official language of Sandnessjoen is Norwegian, but most people also speak English as a second language.</p>'], ARRAY['Hotel Tab Heading','Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum'], ARRAY['Restaurant Tab Heading','Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum'], ARRAY['Attraction Tab Heading','Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum'], ARRAY[''], 2, 1, 14, 1);
INSERT INTO city (name, slug, description_long, description_short, image_link, thumbnail_link, special_offer_image_link, video_link, gallery, getting_around, food_and_drink, facts, hotel, restaurant, attraction, shopping, featured_city, continent, country, region) VALUES ('Santa Marta', 'santa-marta', 'Santa Marta is a city in northern Colombia on the Caribbean Ocean. It was founded in 1525 and is home to the first Spanish settlement in Colombia.Today it is the oldest city in Colombia and the second oldest city in South America.The economy of Santa Marta is based on tourism, trade, port activity, fishing, and agriculture. A large portion of the population is involved in the tourist industry.Santa Marta’s location on the Caribbean coast has helped it become one of the fastest-growing tourist cities in South America.', 'Santa Marta is a city in northern Colombia on the Caribbean Ocean. It was founded in 1525 and is home to the first Spanish settlement in Colombia.', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8d09ccae3db6055d343a1_santa-marta-c1.jpeg', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8d09ccae3db5b76d343a5_santa-marta-c2.jpeg', '', 'https://media-dev.exploro.com/inflight-media/santa-marta-about-banner_0.jpg', ARRAY['https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8d09ccae3dbf587d343a0_santa-marta-overview_0.jpeg','https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8d09ccae3db971ed343a6_santa-marta-c3.jpeg','https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8d09ccae3db230bd343a3_santa-marta-c4.jpeg'], ARRAY['https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8d09ccae3db7265d343a2_santa-marta-getting_0.jpeg','<p id="">Santa Marta is one of the fastest-growing cities in South America.Steps are in place to meet the transportation needs of all the visitors and residents. An above-ground metro system delivers riders to various bus stations to continue their journey.The bus system is well maintained and runs on time.Taxis and rental cars are also available by the hour, day, or week.</p>'], ARRAY['https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8d09ccae3dbbec7d3439f_santa-marta-fnb_0.jpeg','<p id="">The primary entertainment area of Santa Marta is along the coast atnumerous beaches and resorts. Most nightclubs, bars, and restaurants are found along the coast.If you want a taste of Santa Marta, you can find local cuisine in good restaurants all around Santa Marta.If you are looking for local favorites, we recommend the Bandeha Paisa, a rice dish consisting of pork belly and chorizo. Laborers ate this high-calorie meal to give them the energy to do their jobs.</p>'], ARRAY['https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c8d09ccae3db883ed343a4_santa-marta-facts_0.jpeg','<p id="">Colombia has over 4,000 types of orchids.	Colombia is the second most diverse country after Brazil.	The population of Santa Marta is just under half a million people.	Spanish is the official language of Santa Marta.Most residents also speak English as a second language.	Santa Marta has a tropical climate with short and wet winters and long, hot and humid summers.	The currency in Colombia is the Colombian Peso.Currently, 1 US Dollar equals 3.76 Colombian Pesos.	Colombia has the second-highest number of holidays in the world.	A Gallup survey in 2021 indicates the people of Colombia often feel "happy" or "very happy".</p>'], ARRAY['Hotel Tab Heading','Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum'], ARRAY['Restaurant Tab Heading','Lorem ipsum dolor sit amet, consectetur