INSERT INTO continent (name, slug, description_long, description_short, image_link, thumbnail_link, special_offer_image_link, video_link, gallery) VALUES ('Africa', 'africa', 'Africa, a continent of diversity and wonder. From the majestic wildlife of the savannas to the rich cultural heritage of its people, Africa offers an unparalleled experience. Explore the vibrant cities, ancient civilizations, and stunning landscapes that make this continent truly unique. Discover Africa now!', 'Africa, a continent of diversity and wonder. From the majestic wildlife of the savannas to the rich cultural heritage of its people, Africa offers an unparalleled experience. Explore the vibrant cities, ancient civilizations, and stunning landscapes that make this continent truly unique. Discover Africa now!', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63ca064d0b4fe073328ef132_Ol%20Pejeta.jpg', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63ca064d0b4fe073328ef132_Ol%20Pejeta.jpg', 'https://uploads-ssl.webflow.com/63be358a2ba7e25fed479661/63c9971a14afcf29e6e66a0b_63be46e3508ab6f2c169d78e_2ec6dbebf77fe703dc92d325b6d7b461.png', 'https://demo.exploro.com/3rdparty/gde/videos/Nairobi%20Hero.mp4', ARRAY['']);
